Language.Sentence:
The modality of the object (Yes or No):
if yes - only the dialog will be displayed, the background will be hidden;
if no - nothing will be hidden.

Type.SourceCode:
from scripting import server, application, log, session, request, response, VDOM_object

from scripting import e2vdom


class VDOM_dialog_3(VDOM_object):

	def check_unit(self, value):
		return value + "px" if value.isdigit() else value

	def render(self, contents=""):

		idn = (self.id).replace('-', '_')
		id = u'o_' + idn

		e2vdom.process(self)

		position = u"{pos}".format(pos=self.positioning) if self.positioning and self.positioning != "static" else u""

		styles = {
			"z-index": u"%s" % self.zindex if int(self.zindex) != 0 else u"",
			"display": self.displaying,
			"position": position,
			"width": self.check_unit(self.width),
			"height": self.check_unit(self.height),
			"margin": self.margins,
			"padding": self.paddings,
		}

		dialog_style = " ".join(["{}: {};".format(key, value) for key, value in styles.items() if value])

		result = u"""<style type="text/css">
/*defaults*/
.popup-wrapper .title-bar h2{font-size:12px;font-weight:normal;margin:7px 0;padding:0;}
/*style*/
%(css)s</style>""" % {"css": self.style}

		if self.draggable == "0":
			draggable = "true"
		else: draggable = "false"

		if self.show == "1":
			show = "true"
		else: show = "false"

		if self.modal == "0":
			modal = "true"
		else: modal = "false"

		if VDOM_CONFIG_1["DEBUG"] == "1":
			debug_info = u"objtype='%s' objname='%s' ver='%s'" % (self.type.name, self.name, self.type.version)
		else:
			debug_info = u""

		result += u"""			
<div class='popup-wrapper vdom_dialog3 %(classname)s' style='display: none;'>
	<div class='popup-inner'>
		<div %(debug_info)s id='%(id)s' style='%(dialog_style)s'>%(contents)s</div>
	</div>
</div>""" % { "debug_info": debug_info, "id": id, "dialog_style": dialog_style,
				"contents": contents, "classname": self.classname }

		result += """<script type='text/javascript'>
$(document).ready(function(){
	if (typeof dialog_win_%(id)s !== 'undefined') {
		dialog_win_%(id)s.hide();
		delete(dialog_win_%(id)s);
	}
	dialog_win_%(id)s=new Popup($('#%(id)s'),{
		width: '%(width)s'
		,height: '%(height)s'
		,title: "%(title)s"
		,closeText: ''
		,show: %(show)s
		,modal: %(modal)s
		,fixed: false
		,userclass: "%(classname)s"
		,afterHide: function(){ 
			execEventBinded("%(idn)s", "hide", {});
			$('#ui-datepicker-div,#ColorDropdown_selector').fadeOut(); 
		}
		,afterShow: function(){ 
			execEventBinded("%(idn)s", "show", {}); 
		}
	});
	$('.popup-modal-blackout').scroll(function(){
		$('#ui-datepicker-div').fadeOut(); 
		$('input.formcolorpicker').click(); 
		$('input[name^=formdate_]').focusout(); 
	});
	$('%(id)s .close-button').click(function() {
	    dialog_win_%(id)s.hide();
	});
});
</script>""" % {
			"id": id, "idn": idn, "width": self.width, "height": self.height, "drag": draggable,
			"title": (self.title).replace('"','&quot;'), "show": show, "modal": modal, "classname": (self.classname).replace('"','&quot;') }

		return VDOM_object.render(self, contents=result)

	def wysiwyg(self, contents=""):
		width, height, top, left = [int(self.ide_width), int(self.ide_height), int(self.ide_top), int(self.ide_left)]

		result= \
			u"""<container name="{name}" id="{id}" zindex="{zind}" hierarchy="{hierarchy}" order="{order}" top="{top}" left="{left}" width="{container_width}" height="{container_height}">
				<svg>
					<rect x="0" y="0" width="{width}" height="{height}" stroke="#cccccc" stroke-width="3" fill="#ffffff">
						<rect x="0" y="0" width="{width}" height="40" fill="#e9e9e9"/>
						<line x1="{line_x1}"  y1="16" x2="{line_x2}"   y2="24" style="stroke:#222222"/>
						<line x1="{line_x1}"  y1="24" x2="{line_x2}"   y2="16" style="stroke:#222222"/>
					</rect>
				</svg>
				<text top="10" width="{width}" fontsize="14" color="black" textalign="center">{title}</text>
				{contents}
			</container>""".format(
				id               = self.id, 
				zind             = self.zindex, 
				hierarchy        = self.hierarchy, 
				order            = self.order, 
				top              = top, 
				left             = left, 
				container_width  = width + 3, 
				container_height = height + 3,
				width            = width, 
				height           = height,
				title            = self.title,
				contents         = contents,
				line_x1          = width - 20,
				line_x2          = width - 12,
				name             = self.name
			)

		return VDOM_object.wysiwyg(self, contents=result)

def on_update(object, attributes):
	o = object
	modifications = {}

	for attr in ["left", "width", "height", "top"]:
		if attr in attributes:
			attr_value = attributes[attr]
			obj_value = o.attributes.get(attr, '').lower()

			if obj_value.isdigit() and attr_value.isdigit():
				modifications[attr] = attr_value + "px"
				modifications["ide_"+ attr] = attr_value
			elif obj_value.endswith("px") and (attr_value.isdigit() or "px" in attr_value):
				modifications[attr] = attr_value.rstrip("px") + "px"
				modifications["ide_"+ attr] = attr_value.rstrip("px")
			elif attr_value.isdigit() or "px" in attr_value:
				modifications[attr] = attr_value if "px" in attr_value else obj_value
				modifications["ide_"+ attr] = attr_value.rstrip("px")
			else:
				modifications[attr] = attr_value

	attributes.update(modifications)

	users = u"""\
#%(id)s {

}
"""

	default = """
.popup-wrapper { 
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    visibility: visible;
    opacity: 1;
}
.popup-wrapper.fixed { position: fixed; }
.popup-modal-blackout { 
    position: fixed;
    overflow: auto;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
}

.popup-wrapper .title-bar {
    position: relative;
    background-color: #e9e9e9;
    padding: 1px;
    font-size: 10px;
    text-align: center;
}
.popup-wrapper .title-bar a.close{
    outline: none;
    position: absolute;
    background: url("/3938bd92-e009-2767-09e2-1da56035c24a.png") -95px -128px;
    height: 18px;
    width: 19px;
    top: 6px;
    right: 6px;
}
.popup-wrapper .title-bar a.close:hover{
    border: 1px inset #999999;
}
.popup-inner {
    background-color: #fff;
    border: 3px solid #ccc;
}"""

	skin_mapping = {
		"0": users,
		"1": "",
		"2": default
	}
	if "skin" in attributes:
		skin = attributes["skin"]
		if skin in skin_mapping:
			attributes.update(style=skin_mapping[skin])
		else:
			attributes.update(skin="0")

	if "style" in attributes and attributes.get("style") not in skin_mapping.values():
		attributes.update(skin="0")

	return ""

def on_compile(object, attributes):
	for attr in ["left", "width", "height", "top"]:
		obj_value = object.attributes[attr]

		if obj_value.isdigit() or "px" in obj_value:
			object.attributes[attr] = obj_value.rstrip("px") + "px"
			object.attributes["ide_"+ attr] = obj_value.rstrip("px")

