import re

class VDOM_formtext(VDOM_object):

	def check_unit(self, value):
		return value + "px" if value.isdigit() else value

	def render(self, contents=""):
		display = u"none" if self.visible == "0" else u""

		if self.mode == "1":
			mode = u"""readonly="readonly" """
		elif self.mode == "2":
			mode = u"""disabled="disabled" """
		else:
			mode = u""

		auto = u"off" if self.autocomplete == "0" else u"on"

		clean_id = (self.id).replace('-', '_')
		id = u"o_%s" % clean_id

		# for doctype xhtml 1.0 - width|height depends on padding
		# fixw = int(self.width) - 10
		# fixh = int(self.height) - 4

		border = u"0px" if self.multiline == "0" and self.border == "0" else u""

		required = 'required' if self.required=='1' else u''

		pattern = u'pattern="{}"'.format(self.pattern) if self.pattern else ""
		titlepattern = u'title="{}"'.format(self.titlepattern) if self.titlepattern else ""

		maxlength = u'maxlength="{}"'.format(self.maxlength) if self.maxlength else ""
		minlength = u'minlength="{}"'.format(self.minlength) if self.minlength else ""
		inputtype = self.inputtype
		maxvalue = u'max="{}"'.format(self.maxvalue) if self.minlength else ""
		minvalue = u'min="{}"'.format(self.minvalue) if self.minlength else ""

		styles = {
			"width": self.check_unit(self.width),
			"height": self.check_unit(self.height),
			"margin": self.margins,
			"padding": self.paddings,
			"top": self.check_unit(self.top),
			"left": self.check_unit(self.left),
			"z-index": self.zindex,
			"position": self.positioning,
			"display": display,
			"border-width": border
		}
		if self.positioning == "static":
			styles["top"] = styles["left"] = ""
			styles["position"] = ""

		styles_str = " ".join(["{}: {};".format(key, value) for key, value in styles.items() if value])

		if self.title == "":
			title = u""
		else:
			title = u"title=\"%s\"" % (self.title).replace('"', '&quot;')

		if self.placeholder == "":
			placeholder = u""
		else:
			placeholder = u"placeholder=\"%s\"" % (self.placeholder).replace('"', '&quot;')

		if VDOM_CONFIG_1["DEBUG"] == "1":
			debug_info = u"objtype='formtext' objname='%s' ver='2012-02-6'" % self.name
		else:
			debug_info = u""

		if self.multiline == "0":
			result = u"""
				<input {debug_info} {required} id="{id}" name="{name}" 
					style="{style}" tabindex="{tabind}" 
					value="{value}" class="vdom_formtext {classn}" autocomplete="{auto}" 
					type="{inputtype}" {maxvalue} {minvalue} {pattern} {maxlength} {minlength} 
					{mode} {title} {placeholder} 
				/>
			""".format(
				id=id, name=self.customname or self.name, style=styles_str, 
				tabind=self.tabindex, debug_info=debug_info,
				value=unicode(self.value).replace('"', '&quot;'), 
				classn=self.classname, auto=auto, mode=mode, 
				placeholder=placeholder, title=title, 
				required=required, pattern=pattern,
				maxlength=maxlength, minlength=minlength,
				inputtype=inputtype, maxvalue=maxvalue, minvalue=minvalue
			)
		else:
			result = u"""
				<textarea {debug_info} {required} id="{id}" name="{name}" 
					tabindex="{tabind}" style="{style}" class="vdom_formtext {classn}" 
					type="{inputtype}" {pattern} {maxlength} 
					{minlength} {mode} {title} {placeholder} 
          {maxvalue} {minvalue} 
				>{value}</textarea>
			""".format(
				id=id, name=self.customname or self.name, tabind=self.tabindex, 
				debug_info=debug_info, style=styles_str, classn=self.classname, 
				mode=mode, value=self.value, placeholder=placeholder, title=title,
				required=required, pattern=pattern,
				maxlength=maxlength, minlength=minlength,
				inputtype=inputtype, maxvalue=maxvalue, minvalue=minvalue
			)

		focused = u"$q('#%s').focus();" % id if self.focused == "1" else ""

		result += u"""<script type='text/javascript'>
$(document).ready(function(){
	$('#%(id)s').blur(function(){
		var x = $.trim($(this).val());
		execEventBinded("%(clean_id)s", "blur", { itemValue: x, charCount: x.length });
	});
	$('#%(id)s').focusin(function(){
		execEventBinded("%(clean_id)s", "focus", {});
	});
	%(focused)s
});</script>""" % { "id": id, "clean_id": clean_id, "focused": focused }

		return VDOM_object.render(self, contents=result)

	def regex(self, obj):
		match = re.search(r'\d+', obj)
		return int(match.group()) if match else ""

	def wysiwyg(self, contents=""):
		width, height, self.top, self.left = map(self.regex, (self.width, self.height, self.top, self.left))

		self.width = int(width) if width else 130
		self.height = int(height) if height else 22

		disabled = u""" disabled="disabled" """ if self.mode=="2" else u""
		border = u"border-width:0;" if self.multiline == "0" and self.border == "0" else u""
		placeholder = u"placeholder=\"%s\"" % (self.placeholder).replace('"', '&quot;') if self.placeholder!="" else u""

		style = u""" style="width: {width}px; height: {height}px; background-color:#ffffff; font: 14px tahoma; padding: 2px 5px; {border}" """.format(
			border = border,
			width = self.width, height = self.height)

		if self.multiline == "1" : #multiline
			text = \
				u"""<textarea objtype="formtext" {style} {disabled} {placeholder}>
						{value}
					</textarea>
				""".format(
						width = self.width, height = self.height,
						value = self.value, placeholder = placeholder,
						disabled = disabled, 
						style = style)
		else : #singleline
			text = u"""<input objtype="formtext" value="{value}" {style} {disabled} {placeholder}/>""".format(
				width = self.width, height = self.height,
				value = self.value, placeholder = placeholder,
				disabled = disabled,
				style = style)

		result = \
			u"""<container name="{name}" id="{id}" visible="{vis}" zindex="{zind}" hierarchy="{hierarchy}" order="{order}" 
						top="{top}" left="{left}" width="{width}" height="{height}" >
					<htmltext top="0" left="0" width="{width}" height="{height}" overflow="hidden" blendMode="normal">
						{text}
					</htmltext>
				</container>
			""".format(
					id = self.id, vis = self.visible, zind = self.zindex, 
					hierarchy = self.hierarchy, order = self.order, 
					top = self.top, left = self.left, name = self.name,
					width = self.width, height = self.height,
					text = text)

		return VDOM_object.wysiwyg(self, contents=result)


# def set_attr(app_id, object_id, param):
def on_update(object, attributes):

	o = object
	modifications = {}

	for attr in ["left", "top", "width", "height"]:
		if attr in attributes:
			new_value = attributes.get(attr)
			unit = re.findall("[a-z%]+", o.attributes.get(attr, ""))
			unit = unit[0] if unit and unit[0]!="auto" else "px"
			if new_value.isnumeric():
				new_value = new_value + unit

				if attr == "width" and int(new_value.rstrip(unit)) < 10:
					new_value = "10" + unit
				elif attr == "height" and int(new_value.rstrip(unit)) < 4:
					new_value = "4" + unit

			modifications[attr] = new_value
	attributes.update(modifications)

	return ""
