class VDOM_monacoeditor(VDOM_object):
    def render(self, contents=""):
        """
            Args:
                contents (str): Content into this type.
        """
        WOID = (self.id).replace('-', '_')
        ID = 'o_' + WOID

        DEBUG_INFO = {
          "objname": self.name,
          "objtype": self.type.name,
          "id": ID,
          "version": self.type.version
        }
        debug_info_string = " ".join(
            ["{}='{}'".format(key, value) for key, value in DEBUG_INFO.items()]
        )

        DISPLAY_CONVERT = lambda v: "none" if v == "0" else None
        SIZE_CONVERT = lambda v: str(v) + "px"
        STYLES = {
            "display": DISPLAY_CONVERT(self.visible),
            "position": self.positioning1,
            "z-index": self.zindex,
            "top": SIZE_CONVERT(self.top),
            "left": SIZE_CONVERT(self.left),
            "width": SIZE_CONVERT(self.width),
            "height": SIZE_CONVERT(self.height),
        }
        STYLES_STRING_FORMAT = ";".join(
            ["{}:{}".format(key, value) for key, value in STYLES.items() if value is not None]
        )
        STYLES_STRING = u"""
        <style>
            .%(id)s {
                %(styles)s
            }
        </style>
        """ % { "id": ID, "styles": STYLES_STRING_FORMAT }
        STYLES_STRING = STYLES_STRING.replace(" ", "")

        script = u"""
        <script type="text/javascript">
            const loadMonacoEditor = (url, callback) => {
                const script = document.createElement('script');
                script.type = 'text/javascript';
                script.src = url;
                script.onload = callback;

                document.head.appendChild(script);
            };

            const doNothing = () => {};

            loadMonacoEditor('https://cdnjs.cloudflare.com/ajax/libs/monaco-editor/0.47.0/min/vs/loader.min.js', () => {
                /*
                 * @function generateEvent  This function generate E2VDOM event.
                 * @param    {String} name           name of event.
                 * @param    {Object} payload        payload of event.
                 * @return   {null}                  return nothing
                 */
                function generateEvent(name, payload) {
                    execEventBinded("%(woid)s", name, payload);
                }

                require.config({ paths: { 'vs': 'https://cdnjs.cloudflare.com/ajax/libs/monaco-editor/0.47.0/min/vs' }});
                window.MonacoEnvironment = { getWorkerUrl: () => proxy };

                let proxy = URL.createObjectURL(new Blob(
                    [`
                        self.MonacoEnvironment = { baseUrl: 'https://cdnjs.cloudflare.com/ajax/libs/monaco-editor/0.47.0/min/' };
                        importScripts('https://cdnjs.cloudflare.com/ajax/libs/monaco-editor/0.47.0/min/vs/base/worker/workerMain.js');
                    `],
                    { type: 'text/javascript' }
                )
            );

            require(["vs/editor/editor.main"], () => {
                const config = {
                    values: JSON.parse(`%(values)s` || '{}'),
                    theme: "vb", // ["vs", "vs-dark", "hc-black"]
                    textAreaName: "%(name)s",
                    hover: {delay: 3000, enabled: true}
                }
                window.monacoEditor = new MonacoEditor(document.getElementById("%(id)s"), config);
            });

            generateEvent("load", {param: "I am loaded"});
        });
        </script>
        """ % {
          "id": ID,
          "woid": WOID,
          "values": self.value,
          "name": self.name
        }

        RESULT = u"""
        <div {debug_string} class='{id} {classname}'>
            {styles}
            {script}
        </div>
        """.format(
            debug_string=debug_info_string,
            classname=self.cssclass,
            id=ID,
            styles=STYLES_STRING,
            script=script
        )

        return VDOM_object.render(self, contents=RESULT)

    def wysiwyg(self, contents=""):
        from scripting.legacy.wysiwyg import get_empty_wysiwyg_value

        image_id = "30b2054e-61bc-46dc-8f7b-263540d8eaaa"
        result = get_empty_wysiwyg_value(self, image_id)

        return VDOM_object.wysiwyg(self, contents=result)
