Actions.Action:
import ProAdmin from class_db import Database from VEE_core import engine import managers import clear
def clear_all(): clear.clear_all()
#clear web-dav cache #managers.webdav_manager.clear()
# # clear database # Database.maindb().clean() # Database.maindb().commit( 'VACUUM' ) # # Database.macrosdb().clean() # Database.macrosdb().commit( 'VACUUM' ) # # # clear ProAdmin scheme # ProAdmin.scheme().delete() # # # clear application storage # application.storage.rmtree('.') # # # clear all listeners and timers in VEE_core # engine.clear_all()
try:
# check admin rights user = ProAdmin.current_user() if not user: raise Exception()
if not ProAdmin.application().rules(user, 'a'): raise Exception()
# check entered password password = request.arguments.get('password', '')
if not user.check_password(password): raise Exception()
clear_all() self.accept_dialog.text_warning.value = 'Clearing complete'
except Exception, ex: self.action('goTo', '/login')

import ProAdmin from prosuite_localization import get_localization from utilities import get_logout_back_url from prosuite_settings import settings
#response.redirect("/home")
lang = get_localization() login_params = settings.login_params
self.title = lang["clear_page_title"] self.accept_dialog.classname += login_params["class_logo"] self.accept_dialog.text_warning.value = lang["clear.dialog.warning_text"]%application.name self.accept_dialog.form.text_password_label = lang["clear.dialog.password_text"] self.accept_dialog.form.accept_button.label = lang["clear.dialog.clear_button"]%application.name
try: # check admin rights user = ProAdmin.current_user() if not user: raise Exception()
if not ProAdmin.application().rules( user, 'a' ): raise Exception() except: back_url = get_logout_back_url() response.redirect( '/logoff?back_url=%s' % back_url )

