  $(".change_product_holder").click(function () {
    if ($(this).hasClass("expanded")) {
      $(this).toggleClass("expanded");
      $(".change_product").removeClass("expanded");
      $(".change_product li:not(:first)").addClass("hide");
    } else {
      $(this).toggleClass("expanded");
      $(".change_product").addClass("expanded");
      $(".change_product li").removeClass("hide");
    }
  });

  $(".list-item-proshare").click(function () {
    $(".change_product li").addClass("hide");
    $(".change_product").removeClass("expanded");
    $(this).removeClass("hide");
  });

