// ******** PLUGIN PAGE **********
//expand plutgin options
function expand_plugin(data) {
  const cellHeader = data[0];
  if (cellHeader != "Options") return;
  const cellData = data[1];
  const cellNumber = cellData.match(/cell-number="(\d*)"/)[1];
  hide_all_options(cellNumber);
  $("tr[index=" + cellNumber + "]")
    .children()
    .toggleClass("expanded");
}

//hide all options on page
function hide_all_options(cellNumber) {
  $("tr[index!=" + cellNumber + "]")
    .children()
    .removeClass("expanded");
}
//********* END PLUGIN PAGE *********

