from class_macro import Macros
from class_plugins import Plugins
from VEE_resources import ResourceFolderManager
import StringIO
from prosuite_localization import get_localization
from widget_plugin_details import PluginDetailsWidget


lang = get_localization()

def get_context(x):
	return "".join(x.split("-"))

def check(macro):
	from VEE_tools import compile, register_lib, VScriptComlipationError, PythonCompilationError
	try:
		libs = macro.uses()
		for lib in libs:
			if not lib.bytecode:
				disp = VEE_dispatcher(lib)
				disp.compile()
				register_lib(
					disp.name,
					disp.cache,
					disp.debuginfo,
					disp.environment(),
					get_context(disp.namespace)
				)
		compile(source, context=get_context(macro.plugin_guid))
		return "Passed well."
		self.growl.action('show', ["Message", u"No errors in code."])
	except VScriptComlipationError as error:
		return u"VScript Compilation Error (Line {line}): {msg}".format(
			line=error.line,
			msg=error.message
		)
	except PythonCompilationError as error:
		return u"Python Compilation Error: {msg}".format(msg=error.message)

tab_type, tab_id = PluginDetailsWidget.active_tab

if tab_type == "macros":
    macro = Macros.get_by_id(tab_id)
    plugin = Plugins.get_by_guid(macro.plugin_guid)
    if plugin and macro:
        source = request.arguments.get("codeeditor_macros_body", "")
        macro.code = source
        macro.save()
        result = check(macro)
        self.growl.action('show', [lang["message.title"], lang["plugins.codeeditor.save.succsess"].format(result)])
    else:
        self.growl.action('show', [lang["error.title"], lang["plugins.codeeditor.save.error"]])
elif tab_type == "res":
    res_name = tab_id
    plugin = Plugins.get_by_id(request.shared_variables['plugin_id'])
    if plugin and res_name:
        source = request.arguments.get("codeeditor_macros_body", "")
        str_descritor = StringIO.StringIO()
        str_descritor.write(source.encode('utf8'))
        ResourceFolderManager(plugin.guid).import_res(res_name, str_descritor.getvalue())
        self.action('custom', '''
			$('[objname=third_column] iframe')[0].contentWindow.location.reload(true);
        ''')
        self.growl.action('show', [lang["message.title"], lang["plugins.resource.save.succsess"]])
    else:
        self.growl.action('show', [lang["error.title"], lang["plugins.resource.save.error"]])
else: #ctrl+s when editor is not opened
	pass
    #self.growl.action('show', [lang["error.title"], lang["error.unknown_error"]])

