if (!(window.mozInnerScreenX == null)) {
  /* is firefox */
  window.onkeypress = function (event) {
    if (event.charCode === 115 && event.ctrlKey) {
      event.preventDefault();
      execEventBinded("83fe6476_daa6_4e02_ae22_4c6858ccc6ed", "click");
      alert("'Save As' dialog suppressed!");
    }
  };
} else {
  jQuery(document).keydown(function (event) {
    var currKey = 0,
      e = e || event;
    currKey = e.keyCode || e.which || e.charCode; //do this handle FF and IE
    if (
      !(
        String.fromCharCode(event.which).toLowerCase() == "s" && event.ctrlKey
      ) &&
      !(event.which == 19)
    )
      return true;
    event.preventDefault();
    $(".form_macros").submit();
    return false;
  });
}

class PluginDetailsRouter {
  static change_route() {
    const urlSearch = new URLSearchParams(location.search);
    if (E2VSV?.active_tab) {
      urlSearch.set("active_tab", JSON.stringify(E2VSV.active_tab));
    } else {
      urlSearch.delete("active_tab");
    }
    if (E2VSV?.opened_tabs && (E2VSV?.opened_tabs || []).length > 0) {
      urlSearch.set("opened_tabs", JSON.stringify(E2VSV?.opened_tabs || []));
    } else {
      urlSearch.delete("opened_tabs");
    }

    window.history.pushState(
      {},
      document.title,
      `${location.pathname}?${urlSearch}`
    );
  }
}

