lang = get_localization() set_vdom_container(self)
@authenticated @administrator @error_handler def main(): def invoke_dispather(macros): invoke_disp = InvokeDispatcher() invoke_disp.page = self invoke_disp.growl = self.growl invoke_disp.xmldialog = self.xmldialog invoke_disp.macros = macros
current_page = v_currentpage() current_page.page_name = self.name
