plugin_id = session["plugin_id"] = request.arguments.get("keyField", "") cell_name = request.arguments.get("headerData", "")
if plugin_id: plugin = Plugins.get_by_id(plugin_id) if cell_name and plugin:
if cell_name == "Update": self.dialog_update_plugin.title = lang["plugins.dialog_update_plugin"] self.dialog_update_plugin.formbutton_update.label = lang["plugins.dialog_update_plugin"] self.dialog_update_plugin.text_description.action("setText", [lang["plugins.dialog.update_plugin.label"]%(plugin.name)]) self.dialog_update_plugin.form.formtext_pluginid.action("setValue", [plugin.id]) self.dialog_update_plugin.action("show")
elif cell_name == "Export": self.action("goTo", ["/plugins?op=export&plugin_id={}".format(plugin.id)])
elif cell_name == "Plugin_info": create_plugin_dir(plugin.guid) self.action("goTo", ["/plugin_details?id=%s"%plugin_id])
elif cell_name == "Delete": session["plugin_id"] = plugin_id self.dialog_uninstall.title = lang["plugins.dialog_uninstall_plugin"] self.dialog_uninstall.text_uninstall.value = lang["plugins.dialog_uninstall_plugin.text"] self.dialog_uninstall.button_apply.text = lang["plugins.dialog_uninstall_plugin"] self.dialog_uninstall.button_cancel.text = lang["form.buttons.cancel"] self.dialog_uninstall.action("show")
elif cell_name == "MD5": self.dialog_md5.title = lang["message.title"] self.dialog_md5.text_md5.action("setText", [plugin.get_md5()]) self.dialog_md5.button_close.text = lang["form.buttons.close"] self.dialog_md5.action("show")
elif cell_name == "Config": macros = Macros.get_config_macro(plugin.guid) if macros: invoke_dispather(macros)
main()

