from proadmin_sso import SSOClient
import ProAdmin
from prosuite_localization import get_localization
from widget_user_group_dialog import authenticated, error_handler, administrator, set_vdom_container


set_vdom_container(self)


@authenticated
@administrator
@error_handler
def main():
    LANG = get_localization()
    host = request.shared_variables["host"]
    if host:
        host = host.replace("https://", "").replace("http://", "").replace("/", "")
        ssoclient = SSOClient(request, response)
        ssoclient.set_action_mode(self)
        ssoclient.test_sso(url=ProAdmin.hosts(url=host)[0])
        session["sso_tested"] = "1"
    else:
        self.growl.action("show", [LANG["error.title"], LANG["remotescheme.not_all_params_filled.text"]])

main()

