TITLE_TEMPLATE = """
<div style='display: flex;  align-items: center'>
	<img style='margin-right: 10px;width: 60px !important;height: 60px !important;background-position: center;float: left;background: #ffffff;-webkit-box-shadow: 0px 0px 18px rgba(207, 216, 220, 0.53);-moz-box-shadow: 0px 0px 18px rgba(207, 216, 220, 0.53);-o-box-shadow: 0px 0px 18px rgba(207, 216, 220, 0.53);
