class ProSuiteMacrosListWidget(object):
	def __init__(self, page, plugin_guid):
		self.page = page
		self.dialog = self.page.plugin_panel.plugin_panel_dialog
		self.plugin = Plugins.get_by_guid(plugin_guid)
		self.list = self.dialog.macroses
	
	def get_item_value(self, item):
		return """
		<div>
			<img src="{icon}">
			<span>{label}</span>
		</div>
		""".format(
			icon=WidgetMacros.get_macros_icon_url(item),
			label=item.name.encode("utf-8")
		)

	def get_item_id(self, item):
		return "m_" + str(item.guid)
	
	def render(self):
		self.list.data = json.dumps({
			self.get_item_id(m): self.get_item_value(m) for m in filter(
				lambda x: x.on_board == "0" and x.is_button_macros == "1", Macros.get_macros_by_plugin_guid(self.plugin.guid)
			)
		})
		self.dialog.action('setTitle', TITLE_TEMPLATE.format(
			WidgetPlugins.get_plugin_image_url(self.plugin), self.plugin.name)
		)
		self.dialog.action('show')

import json
from collections import OrderedDict
from class_plugins import Plugins
from widget_macro import WidgetMacros
