CONFIG_MACRO_NAME = "config"
DEFAULT_VDOM_CLASS_ID = "1ee59eba-2a1a-4ff5-bc2b-061ff190f4d1"

class ProSuiteMacrosWidget(object):
    def __init__( self ):
        self.pictures = []
        self.buttons = []

    def fetch_data( self, page_name, plugins=None, plugin_macro=None ):

        if not plugins:
            plugins = Plugins.get_all()

        for plugin in plugins:
            plg_button_added = False

            if not plugin_macro:
                plugin_macros = plugin.get_macros()
            else:
                plugin_macros = plugin_macro[plugin.guid]

            for macros in plugin_macros:
                if macros.is_button_macros == "1" and \
                   macros.page == page_name and \
                   macros.name != CONFIG_MACRO_NAME:
                    if macros.on_board == "1":
                        self.pictures.append(WidgetMacros.get_macros_icon_url(macros))
                        self.buttons.append({
                            "guid": "m_" + macros.guid,
                            "data": json.dumps({
                                "name": macros.name,
                                "icon": WidgetMacros.get_macros_icon_url(macros),
                                "description": macros.description
                            }),
                            "position"  : macros.zindex,
                        })
                    elif macros.on_board == "0" and not plg_button_added:
                        plg_button_added = True
                        self.buttons.append({
                            "guid": "p_" + plugin.guid,
                            "data": json.dumps({
                                "name": plugin.name,
                                "icon": WidgetPlugins.get_plugin_image_url(plugin),
                            }),
                            "position": plugin.zindex,
                        })

    def get_data(self):
        return OrderedDict([(item['guid'], item['data']) for item in sorted(self.buttons, key=lambda k: k['position'])])

    def set_img( self ):
        from cStringIO import StringIO
        html_buffer = StringIO()
        html_buffer.write( "<div class='img-grid'>" )
        html_buffer.writelines( [ "<img src='%s'/>" % pic_guid for pic_guid in self.pictures[:4] ] )
        html_buffer.write( "</div>" )
        return html_buffer.getvalue()

    def render(self, objectview, img_holder=None, header_button=None, page_name=None, plugins=None, plugin_macro=None):
        self.fetch_data(page_name, plugins, plugin_macro)
        if self.buttons:
            objectview.vdomclassid = DEFAULT_VDOM_CLASS_ID
            objectview.data = json.dumps(self.get_data())
            if img_holder:
            	img_holder.htmlcode = self.set_img()
        else:
        	pass
            #header_button.visible = "0"

