class VdomEventEngine: def __init__( self ): self.__exec_lock = threading.Lock() self.__init_event = threading.Event() self.listeners = {} self.timers = {} self.setup_logging()
def wait_init(self, timeout = None): self.__init_event.wait(timeout)
