from VEE_triggers import engineTimeTrigger, compilerTimeTrigger self.put_event(VEE_StartEngineEvent())
self.engine_logger.info("Starting engine thread...") engineTimeTrigger().start()
self.engine_logger.info("Starting compiler thread...") compilerTimeTrigger().start()
self.engine_logger.info( u"Engine started." )
def engine_stop( self ): self.engine_logger.info( u"Engine stopped." ) if queue_thread: queue_thread.stop() if compiler_thread: compiler_thread.stop() self.clear_all()
