def put_event( self, event ): # enqueue application events only if there are listeners for them #print "@@@@@@@@@@@@@"+str((event.__class__, hash(event))) if isinstance(event, VEE_BaseAbstractEvent) and \ (event.__class__, hash(event)) not in self.listeners: print "NoDispatcher" return workflow_queue.put( event )
def process_event( self, event ): with self.__exec_lock: key = ( event.__class__, hash( event ) ) if key in self.listeners: dispatchers = self.listeners[ key ] self.engine_logger.debug( u"Event '{className}' has {dispatchersCount} dispatchers".format( className = event.__class__.__name__[4:], dispatchersCount = len( dispatchers ) ) ) for dispatcher in dispatchers.itervalues(): dispatcher( event ) else: self.engine_logger.debug( u"No dispatcher for given event '{className}'".format( className = event.__class__.__name__[4:] ) )
def process_queue( self ): event = None try: event = workflow_queue.get( True, 0.5 ) except Queue.Empty: self.check_timers() return 2.0
if isinstance( event, VEE_TimerEvent ): self.engine_logger.debug( u"Recieved event of class '{timeClass}' with name '{timerName}'".format( timeClass = event.__class__.__name__[ 4: ], timerName = event.timer.timer.name.split( ":", 1 )[ 1 ] ) )
else: #self.engine_logger.info( ( u"Recieved event of class '{className}' ({cls}:{hash})".format( # className = event.__class__.__name__[ 4: ], # cls = event.__class__.__name__, # hash = hash( event ) ) ) ) self.engine_logger.debug( ( u"Recieved event of class '{className}'".format( className = event.__class__.__name__[ 4: ] ) ) )
if isinstance( event, VEE_StopEngineEvent ): # reset internal event self.__init_event.clear() self.engine_stop() return
elif isinstance( event, VEE_StartEngineEvent ): self.load_listeners() # set internal event so that waiting threads can resume self.__init_event.set() return
self.process_event( event )
#check for webdav activity # try: # from webdav_server.vdom_dav_provider import get_properties # if (datetime.now() - get_properties.last_access()).total_seconds()<30: # return 10.0 # except Exception as e: # return 1.0
return 0.25
def get_dispatcher_by_event( self, event ): key = ( event.__class__, hash( event ) ) if key in self.listeners: return self.listeners[ key ].values()[ 0 ]
return None
def do_compile(self): task = None try: task = compiler_queue.get( True, 0.5 ) except Queue.Empty: return
macro, event_class, key, dispatcher = task #self.engine_logger.debug("%s[%s] - start compiling", macro.name, macro.guid) ret = dispatcher.compile() if ret == COMPILATION_SUCCESS: self.compile_done(macro, event_class, key, dispatcher) self.engine_logger.debug(u"%s[%s] - compiling done", macro.name, macro.guid) # self.engine_logger.info("Compiling done" ) else: self.engine_logger.debug(u"%s[%s] - compiling failing", macro.name, macro.guid) # self.engine_logger.info("Compiling failed" ) #cache, code = compile( source , environment = env ) return 0.01
def compile_done(self, macro, event_class, key, dispatcher): macro.bytecode = (dispatcher.cache, dispatcher.debuginfo) macro.save_after_compile() self.register_dispatcher(event_class, key, dispatcher)
def compile_and_register(self, macro, event_class, key): try: dispatcher = VEE_dispatcher(macro) #if macro.bytecode: # self.engine_logger.debug("%s[%s] - bytecode exists", macro.name, macro.guid) # if dispatcher.type != Macros.MacrosType.LIBRARY or not lookup_lib(dispatcher.name, dispatcher.get_context()): # self.register_dispatcher(event_class, key, dispatcher) # return COMPILATION_SUCCESS
self.engine_logger.debug(u"%s[%s] - put macro in compile queue", macro.name, macro.guid) compiler_queue.put((macro, event_class, key, dispatcher))
except Exception as e: self.engine_logger.exception(u"@@@@@@@@@Error while vscript compilation." )
def register_dispatcher( self, event_class, event_key, dispatcher ): key = ( event_class, event_key ) disp_hash = hash( dispatcher.guid ) # self.info( u"Register dispatcher '{dispatcherName}' for event '{eventName}' ({cls}:{hash})".format( # dispatcherName = dispatcher.name, # eventName = event_class.__name__[ 4: ], # cls = event_class.__name__, # hash = event_key ) )
if dispatcher.type == Macros.MacrosType.LIBRARY: unregister_lib(dispatcher.name, dispatcher.get_context()) register_lib(dispatcher.name, dispatcher.cache, dispatcher.debuginfo, dispatcher.environment(), dispatcher.get_context()) self.engine_logger.debug( u"Register library '{dispatcherName}' in namespace '{eventName}'".format( dispatcherName = dispatcher.name, eventName = dispatcher.namespace ) ) else: self.engine_logger.debug( u"Register dispatcher '{dispatcherName}' for event '{eventName}'".format( dispatcherName = dispatcher.name, eventName = event_class.__name__[ 4: ] ) )
if key not in self.listeners: self.listeners[ key ] = {}
self.listeners[ key ][ disp_hash ] = dispatcher
def unregister_dispatcher( self, event_class, event_key, disp_guid ): key = ( event_class, event_key ) if key in self.listeners: dispatchers = self.listeners[ key ] disp_hash = hash( disp_guid )
if disp_hash in dispatchers: dispatcher = dispatchers[ disp_hash ] del dispatchers[ disp_hash ]
self.engine_logger.debug( u"Unregister dispatcher '{dispatcherName}' for event '{eventName}'".format( dispatcherName = dispatcher.name, eventName = event_class.__name__[ 4: ] ) )
if len( dispatchers ) == 0: del self.listeners[ key ]
def clear_all(self): #self.clear_log() fixme self.listeners = {} self.timers = {} try: while True: workflow_queue.get_nowait() except Queue.Empty: pass
def add_timer( self, name, delay, hash_value ): """Add timer with defined name and delay""" if name not in self.timers: self.timers[ name ] = VEE_timer( name, delay, hash_value )
def update_timer( self, name, delay, hash_value ): """Replace timer with defined name and delay""" self.timers[name] = VEE_timer( name, delay, hash_value )
def delete_timer( self, name ): """Removing timer. If there is no with such name - no exception rising""" try: del self.timers[ name ] except Exception: pass
def activate_timer( self, name, state ): """Timer change state to active. If there is no with such name - exception raised""" try: self.timers[ name ].active = state except Exception: raise Exception( u"No timer with such name" )
def check_timers(self): for timer_name in self.timers: if self.timers[ timer_name ].check(): self.put_event( VEE_TimerEvent( self.timers[ timer_name ] ) )
def get_timer_by_name( self, name ): return self.timers.get( name, None )
