# Base class for applications events (except TimerEvent)
class VEE_BaseAbstractEvent(VEE_AbstractEvent):
    def __init__(self):
        self._user = v_proadmin.v_currentuser()

    def get_user(self):
        return self._user

    user = property(get_user)


class VEE_CustomEvent(VEE_AbstractEvent):
    def __init__(self, plugin_guid=None, name=None):
        VEE_AbstractEvent.__init__(self)
        self.name = ""
        self.plugin_guid = ""
        self.data = None

    def __hash__(self):
        return self.get_key(self.plugin_guid, self.name)

    @classmethod
    def get_key(self, plugin_guid, name):
        return hash("{0}:{1}".format(plugin_guid, name))


class VEE_OnPluginUninstall(VEE_BaseButtonEvent):
    def __init__(self, page, plugin_guid, namespace=None, macros_guid=None):
        self.__plugin_guid = plugin_guid
        self.namespace = namespace
        self.macros_guid = macros_guid
        self.page = page
        super(VEE_OnPluginUninstall, self).__init__()

    def __invoke_dispather(self, macroses):
        from VEE_dispatcher import InvokeDispatcher
        import VEE_std_lib

        for mc in macroses:
            evt = VEE_OnPluginUninstall(
                page=self.page,
                plugin_guid=self.__plugin_guid,
                namespace=mc.namespace,
                macros_guid=mc.guid
            )
            invoke_disp = InvokeDispatcher()
            invoke_disp.macros = mc
            invoke_disp.page = self.page
            invoke_disp.xmldialog = getattr(self.page, 'xmldialog', None)
            invoke_disp.growl = getattr(self.page, 'growl', None)
            current_page = VEE_std_lib.v_currentpage()
            current_page.page_name = "Home"
            invoke_disp.current_page = current_page
            invoke_disp.add_env_var("v_event", VEE_std_lib.v_event(evt))
            invoke_disp.run(evt)

    def activate(self):
        from class_macro import Macros
        self.__invoke_dispather(
            Macros.get_macros_by_class_name_and_plugin_guid(
                self.__class__.__name__,
                self.__plugin_guid
            )
        )


class VEE_PluginIDEExtension(VEE_BaseButtonEvent):
    def __init__(self, page, namespace=None, macros_guid=None, macros_id=None, dynamicvdom=None, css_container=None, js_container=None, source_plugin=None):
        super(VEE_PluginIDEExtension, self).__init__()
        self.namespace = namespace
        self.macros_guid = macros_guid
        self.macros_id = macros_id or self.macros_extension_id
        self.page = page
        self.dynamicvdom = dynamicvdom
        self.css_container = css_container
        self.js_container = js_container
        self.macros = None
        self.data = None
        self.namespace = None
        self.plugin_guid = None
        self.source_plugin = source_plugin

    @property
    def nextstate(self):
        return request.shared_variables[inspect.currentframe().f_code.co_name]

    @nextstate.setter
    def nextstate(self, value):
        response.shared_variables[inspect.currentframe().f_code.co_name] = value

    @property
    def custom_name(self):
        return request.arguments.get("customName", "{}")

    @property
    def macros_extension_id(self):
        return request.shared_variables[inspect.currentframe().f_code.co_name]

    @macros_extension_id.setter
    def macros_extension_id(self, value):
        response.shared_variables[inspect.currentframe().f_code.co_name] = value

    @property
    def parameters(self):
        return request.arguments.get(inspect.currentframe().f_code.co_name, "{}")

    def __invoke_dispather(self):
       from VEE_dispatcher import InvokeDispatcher
       import VEE_std_lib
       self.__init_macros()

       display = v_display()
       callback_data = {"name": "", "params": {}}

       if self.nextstate:
           callback_data = {"name": self.custom_name, "params": json.loads(self.parameters)}
           self.data = callback_data
           display._nextstate = self.nextstate

       invoke_disp = InvokeDispatcher()
       invoke_disp.macros = self.macros
       invoke_disp.page = self.page
       invoke_disp.xmldialog = getattr(self.page, 'xmldialog', None)
       invoke_disp.growl = getattr(self.page, 'growl', None)
       current_page = VEE_std_lib.v_currentpage()
       current_page.page_name = "plugin_details"
       invoke_disp.current_page = current_page
       invoke_disp.add_env_var("v_event", VEE_std_lib.v_event(self))
       invoke_disp.add_env_var("v_display", display)
       invoke_disp.run(self)
       self.dynamicvdom.vdomxml = display._vdomxml
       self.dynamicvdom.vdomactions = display._e2vdom
       self.css_container.data = ".dynamicvdom {{ {} }}".format(display._scss or '')
       self.js_container.data = """
       // force dynamic ready function
       dynamicObjectLoading = true;
       $(() => {{ {} }})""".format(
           display._javascript or ''
       )

       self.nextstate = display._nextstate or "Init"
       self.macros_extension_id = self.macros.id

    def activate(self):
        self.__invoke_dispather()

    def __init_macros(self):
        from class_macro import Macros
        mc = Macros.get_by_guid(self.macros_guid) if self.macros_guid else Macros.get_by_id(self.macros_id)
        if not mc:
            raise ValueError("Macros not found")
        if mc and mc.class_name != self.__class__.__name__:
            raise ValueError("Macros is not \"{}\" event listener".format(self.__class__.__name__))
        self.macros_guid = mc.guid
        self.macros_id = mc.id
        self.macros = mc
        self.namespace = mc.namespace
        self.plugin_guid = mc.plugin_guid
        return mc

    @property
    def macroses(self):
        from class_macro import Macros
        return Macros.get_macros_by_class_name(self.__class__.__name__)

    @property
    def plugin(self):
        from VEE_std_lib import v_plugin
        if self.source_plugin:
            return v_plugin(self.source_plugin.guid, obj=self.source_plugin)
        else:
            return v_nothing

from VEE_std_lib import v_buffer, v_BaseException
from VEE_utils import AutoCast
from vscript import generic
import os
import urllib


def fullpath(fn):
    def wrapper(*args, **kwargs):
        arguments = list(args)
        self, path = arguments[:2]
        arguments[1] = os.path.join(self.plugin_path(), path)
        return fn(*arguments, **kwargs)
    return wrapper


TMP_FOLDER = "tmp"
RES_FOLDER = "res"
DB_FOLDER = "db"


class v_res_file(v_buffer):
    def __init__(self, path, mode="rb", file_obj=None):
        v_buffer.__init__(self, "")
        self.mode = "rb"
        self.path = application.storage.abs_path(path)

    def open(self):
        return application.storage.open(self.path, self.mode)


class StorageManager(object):
    @classmethod
    def open(self, path, mode="rb"):
        if self.exists(path):
            return application.storage.open(path, mode)
        else:
            raise Exception("Open: no such path: %s" % path)

    @classmethod
    def delete(self, path):
        if self.exists(path):
            return application.storage.delete(path)
        else:
            raise Exception("Delete: no such path: %s" % path)

    @classmethod
    def write(self, path, fd):
        return application.storage.write(path, fd)

    @classmethod
    def getsize(self, path):
        if self.exists(path):
            return application.storage.getsize(path)
        else:
            raise Exception("GetSize: no such path: %s" % path)

    @classmethod
    def mkdir(self, path):
        if not self.exists(path):
            application.storage.mkdir(path)
        else:
            raise Exception("mkdir: such path already exist: %s" % path)

    @classmethod
    def exists(self, path):
        return application.storage.exists(path)

    @classmethod
    def isfile(self, path):
        return application.storage.isfile(path)

    @classmethod
    def isdir(self, path):
        return application.storage.isdir(path)

    @classmethod
    def listdir(self, path):
        return application.storage.listdir(path)


_PUBLIC_URL = u"/get_plugin_resource?guid={0}&name={1}&type={2}"


class BaseResourceManager(object):
    def __init__(self, plugin_guid):
        self._plugin_guid = plugin_guid
        self._path = ""

    plugin_guid = property(lambda x: x._plugin_guid)
    path = property(lambda x: x._path)

    @fullpath
    def open(self, path, mode="rb"):
        return StorageManager.open(path, mode)

    @fullpath
    def delete(self, path):
        StorageManager.delete(path)

    @fullpath
    def write(self, path, fd):
        return StorageManager.write(path, fd)

    @fullpath
    def size(self, path):
        return StorageManager.getsize(path)

    def mkdir(self, path):
        pass

    @fullpath
    def exists(self, path):
        return self._exists(path)

    def _exists(self, path):
        return StorageManager.exists(path)

    @fullpath
    def isfile(self, path):
        return StorageManager.isfile(path)

    @fullpath
    def isdir(self, path):
        return StorageManager.isdir(path)

    @fullpath
    def listdir(self, path):
        return StorageManager.listdir(path)

    def plugin_path(self):
        return os.path.join(self.plugin_guid, self.path) \
            if self._path else self.plugin_guid

    def abs_path(self, file_name=None):
        return application.storage.abs_path(self.plugin_path())

    def delete_all(self):
        for res in self.listdir("."):
            self.delete(res)

    def public_link(self, name, res_type):
        return _PUBLIC_URL.format(
            self.plugin_guid,
            urllib.quote(name),
            res_type)

    @fullpath
    def get_full_path(self, path):
        return path

    def rename(self, old_name, new_name):
        if self.exists(old_name):
            folder = self.abs_path()
            old = os.path.join(folder, old_name)
            new = os.path.join(folder, new_name)
            os.rename(old, new)

class ResourceManager(BaseResourceManager):
    """Class for manage resources.
            All resources are stored in ./res folders
    """

    def __init__(self, plugin_guid, path):
        BaseResourceManager.__init__(self, plugin_guid)
        self._path = path

    def abs_path(self, file_name=None):
        if file_name is None:
            return BaseResourceManager.abs_path(self)
        return os.path.join(BaseResourceManager.abs_path(self), file_name)

    def public_link(self, name):
        return super(ResourceManager, self).public_link(name, self._path)

    @property
    def resourcelist(self):
        return [
            os.path.relpath(os.path.join(_[0], file_name), self.abs_path())
                for _ in os.walk(self.abs_path())
                    for file_name in _[2]
        ]

    import_res = BaseResourceManager.write
    export_res = BaseResourceManager.open
    delete_res = BaseResourceManager.delete


def ResourceFolderManager(guid): return ResourceManager(guid, RES_FOLDER)


def TemporaryFolderManager(guid): return ResourceManager(guid, TMP_FOLDER)


def create_plugin_dir(guid):
    if not StorageManager.exists(guid):
        StorageManager.mkdir(guid)
        for p in [TMP_FOLDER, RES_FOLDER, DB_FOLDER]:
            StorageManager.mkdir(os.path.join(guid, p))


def delete_plugin_dir(guid):
    if StorageManager.exists(guid):
        application.storage.rmtree(guid)


