class DatabaseDoesntExist(Exception):
    def __init__(self):
        Exception.__init__(self, 'Database doesn\'t exist')


class DatabaseUnknownType(Exception):
    def __init__(self, t):
        Exception.__init__(self, "Database doesn't support '%s' type" % t.__name__)


