class v_event(base_object):
    @CachedProperty
    def event(self):
        return self.object

class v_customevent(v_event):
    def __init__(self, obj=None):
        from VEE_prosuite_events import VEE_CustomEvent
        v_event.__init__(self, obj or VEE_CustomEvent())

    @AutoCast
    @v_PropertySimple
    def v_data(self, value, retVal):
        if retVal:
            return self.event.data
        else:
            self.event.data = value

    @AutoCast
    @v_PropertySimple
    def v_pluginguid(self, value, retVal):
        if retVal:
            return self.event.plugin_guid
        else:
            self.event.plugin_guid = value
    
    @AutoCast
    @v_PropertyReadOnly
    def v_id(self):
        return self.event.id

    @AutoCast
    @v_PropertyReadOnly
    def v_guid(self):
        return self.event.guid

    @AutoCast
    @v_PropertySimple	
    def v_name(self, value=None, retVal=True):
        if retVal:
            return self.event.name
        if value:
            self.event.name = value

    @AutoCast
    def v_save(self):
        self.event.save()

    @AutoCast
    def v_delete(self):
        self.event.delete()

@AutoCast
def v_raiseevent(event_obj, disp):
    if not isinstance(event_obj, v_customevent):
        raise v_BadObjectType(
            "Event must be CustomEvent or APIEvent type, not %s" % event_obj.__class__.__name__)

    if event_obj.event.name:
        event_obj.event.plugin_guid = disp.namespace
        event_obj.event.activate()
    else:
        raise v_BadObjectType("Set event name")


@AutoCast
def v_raiseglobalevent(event_obj):
    from VEE_core import engine

    if not isinstance(event_obj, v_customevent):
        raise v_BadObjectType("Event must be CustomEvent, not %s" % event_obj.__class__.__name__)
    if not event_obj.event.name or not event_obj.event.plugin_guid:
        raise v_BadObjectType("Set event name and plugin guid")

    dispatcher = engine.get_dispatcher_by_event(event_obj.event)
    if not dispatcher: raise v_BadObjectType("No dispatcher for given data")

    class v_response(object):
        def __init__(self):
            self.value = ""

        @AutoCast
        def v_write(self, value):
            self.value = value

    v_responseObj = v_response()
    dispatcher(
        event=event_obj, env_mask=0b1111,
        custom_env=(
            ("v_response", v_responseObj),
        ),
        safe=False
    )
    return v_responseObj.value

class v_timer(generic):

    def __init__(self, obj=None):
        if obj is not None:
            self.object = obj
        generic.__init__( self )
  
    @CachedProperty
    def timer(self):
        return self.object

    @AutoCast
    @v_PropertySimple
    def v_delay(self, value, retVal):
        if retVal:
            return ":".join(map(str, self.timer.delay))
        else:
            self.timer.delay = map(int, value.split(":"))

    @AutoCast
    @v_PropertySimple
    def v_isactive(self, value, retVal):
        if retVal:
            return self.timer.active
        else:
            self.timer.active = value

    @AutoCastCachedProperty
    @v_PropertyReadOnly
    def v_lastrun(self):
        return self.timer.last_run

    @AutoCast
    @v_PropertyReadOnly
    def v_id(self):
        return self.timer.id

    @AutoCast
    @v_PropertyReadOnly
    def v_guid(self):
        return self.timer.guid


    @AutoCast
    @v_PropertySimple	
    def v_name(self, value=None, retVal=True):
        if retVal:
            return self.timer.name
        if value:
            self.timer.name = value

    @AutoCast
    @v_PropertySimple	
    def v_period(self, value=None, retVal=True):
        if retVal:
            return self.timer.period
        if value:
            self.timer.period = value
        
    @AutoCast
    def v_save(self):
        self.timer.save()

    @AutoCast
    def v_delete(self):
        self.timer.delete()

    # obsolete methods names
    v_is_active = v_isactive
    v_last_run = v_lastrun

