class v_TimerDoesntExists(v_BaseException):
    pass


class v_engine(object):
    @staticmethod
    @AutoCast
    def v_activatetimer(timer_name, disp):
        try:
            disp.activate_timer(timer_name)
        except:
            raise v_TimerDoesntExists("Timer doesn't exists")

    @staticmethod
    @AutoCast
    def v_deactivatetimer(timer_name, disp):
        try:
            disp.deactivate_timer(timer_name)
        except:
            raise v_TimerDoesntExists("Timer doesn't exists")

    @staticmethod
    @AutoCast
    def v_gettimer(timer_name, disp):
        t = disp.get_timer_by_name(timer_name)
        if not t:
            raise v_TimerDoesntExists("Timer doesn't exists")
        return v_timer(t)


class v_currentpage(generic):
    def __init__(self):
        generic.__init__(self)
        self.page_name = ""
        self.redirect_url = ""
        self.namespace = ""

    @AutoCastCachedProperty
    @v_PropertyReadOnly
    def v_host(self):
        return "{0}://{1}".format(
            "https" if request.environment.get(
                'SERVER_PORT', '80') == '443' else request.protocol.name.lower(),
            request.server.host)

    @AutoCastCachedProperty
    @v_PropertyReadOnly
    def v_lang(self):
        return session.get("lang")

    @AutoCastCachedProperty
    @v_PropertyReadOnly
    def v_name(self):
        return self.page_name

    @AutoCast
    def v_redirect(self, url):
        self.redirect_url = url

    def v_refresh(self):
        self.v_redirect("/" + self.page_name)

    def __call__(self, *arguments, **keywords):
        if "let" in keywords:
            return self.let(*arguments, **keywords)
        elif "set" in keywords:
            raise errors.type_mismatch
        else:
            return self.get(*arguments, **keywords)

    def _format_key(self, key):
        return u"{0}_{1}".format(self.namespace, key)

    @AutoCast
    def get(self, key, secure=True, *arguments, **keywords):
        if not key:
            raise errors.wrong_number_of_arguments
        return request.shared_variables[self._format_key(key) if secure else key]

    @AutoCast
    def let(self, key=None, secure=True, *arguments, **keywords):
        if not key:
            raise errors.wrong_number_of_arguments
        response.shared_variables[self._format_key(
            key) if secure else key] = keywords["let"]

    @AutoCast
    def erase(self, key, secure=True):
        if not key:
            raise errors.wrong_number_of_arguments
        response.shared_variables[self._format_key(
            key) if secure else key] = None

    @AutoCast
    def __contains__(self, key):
        if not key:
            raise errors.wrong_number_of_arguments
        return self._format_key(key) in request.shared_variables.keys()


class v_plugin(generic):
    def __init__(self, guid, obj=None):
        from VEE_sqlite3 import v_DatabaseManager
        from VEE_resources import v_ResManager
        from class_plugins import Plugins

        if guid is not None: self.object = Plugins.get_by_guid(guid)       
        if obj is not None: self.object = obj
        self.res_manager = v_ResManager(guid)
        self.db_manager = v_DatabaseManager(guid)

        generic.__init__( self )

    @CachedProperty
    def plugin(self):
        return self.object

    @AutoCast
    @v_PropertyReadOnly
    def v_id(self):
        return self.plugin.id

    @AutoCast
    @v_PropertyReadOnly
    def v_guid(self):
        return self.plugin.guid

    @AutoCast
    @v_PropertySimple	
    def v_name(self, value=None, retVal=True):
        if retVal:
            return self.plugin.name
        if value:
            self.plugin.name = value
            

    @AutoCast
    @v_PropertySimple
    def v_description(self, value=None, retVal=True):
        if retVal:
            return self.plugin.description
        if value:
            self.plugin.description = value

    @AutoCast
    @v_PropertySimple	
    def v_author(self, value=None, retVal=True):
        if retVal:
            return self.plugin.author
        if value:
            self.plugin.author = value

    @AutoCast
    @v_PropertySimple
    def v_picture(self, value=None, retVal=True):
        if retVal:
            return self.plugin.picture
        if value:
            self.plugin.picture = value

    @AutoCast
    @v_PropertySimple
    def v_zindex(self, value=None, retVal=True):
        if retVal:
            return self.plugin.zindex
        if value:
            self.plugin.zindex = value

    @AutoCast
    @v_PropertySimple
    def v_version(self, value=None, retVal=True):
        if retVal:
            return self.plugin.version
        if value:
            self.plugin.version = value

    @AutoCast
    @v_PropertySimple
    def v_protected(self, value=None, retVal=True):
        if retVal:
            return self.plugin.protected
        if value:
            self.plugin.protected = value

    @AutoCast
    @v_PropertyReadOnly
    def v_macroses(self):
        return map(lambda x: v_macros(x.guid, x), self.plugin.get_macros())

    @AutoCast
    @v_PropertyReadOnly
    def v_timers(self):
        return map(v_timer, self.plugin.get_timer())

    @AutoCast
    @v_PropertyReadOnly
    def v_customevents(self):
        return map(v_customevent, self.plugin.get_custom_event())

    @AutoCast
    @v_PropertyReadOnly
    def v_databases(self):
        return self.db_manager.v_databaselist()

    @AutoCast
    @v_PropertyReadOnly
    def v_resources(self):
        return self.res_manager

    @classmethod
    @AutoCast
    def v_getbyguid(self, guid):
        from class_plugins import Plugins
        plugin = Plugins.get_by_guid(guid)
        return nothing if plugin is None else v_plugin(plugin.guid, plugin)

    @AutoCast
    def v_getmacrosbyname(self, name):
        macro = Macros.get_macro_by_plugin_guid_and_name(self.plugin.guid, name)
        return nothing if macro is None else v_macros(macro.guid, macro)
            
    @AutoCast
    def v_gettimerbyname(self, name):
        from class_timer import Timer
        timer = Timer.get_timer_by_plugin_guid_and_name(self.plugin.guid, name)
        return nothing if timer is None else v_timer(timer)

    @AutoCast
    def v_getcustomeventbyname(self, name):
        from class_custom_event import CustomEvent
        event = CustomEvent.get_custom_event_by_plugin_guid_and_name(self.plugin.guid, name)
        return nothing if len(event) < 1 else v_customevent(event[0])

    @AutoCast
    def v_getdatabasebyname(self, name):
        return self.db_manager(name) 

    @AutoCast
    def v_save(self):
        self.plugin.save()

