def v_exitscript():
    from VEE_tools import StopExecutionError
    raise StopExecutionError()


@AutoCast
def v_escapestring(data, quote=False):
    return escape(data, quote)


class v_display(object):
    def __init__( self ):
        self.vdomdynobj = None
        self._nextstate = ''
        self._vdomxml = ''
        self._e2vdom = ''
        self._scss = ''
        self._javascript = ''

    @AutoCast
    def v_render(self, vdomxml="", vdomactions="", nextstate=""):
        self._vdomxml = vdomxml if vdomxml else self._vdomxml
        self._e2vdom = vdomactions if vdomactions else self._e2vdom
        self._nextState = nextstate if nextstate else self._nextstate

    @AutoCast
    @v_PropertySimple
    def v_vdomxml( self, value, retVal ):
        if retVal: return self._vdomxml
        else: self._vdomxml = unicode(value)

    @AutoCast
    @v_PropertySimple
    def v_scss( self, value, retVal ):
        if retVal: return self._scss
        else: self._scss = unicode(value)

    @AutoCast
    @v_PropertySimple
    def v_javascript( self, value, retVal ):
        if retVal: return self._javascript
        else: self._javascript = unicode(value)

    @AutoCast
    @v_PropertySimple
    def v_e2vdom( self, value, retVal ):
        if retVal: return self._e2vdom
        else: self._e2vdom = unicode(value) 
    
    @AutoCast
    @v_PropertySimple
    def v_nextstate( self, value, retVal ):
        if retVal: return self._nextstate
        else: self._nextstate = str(value)


