    BuiltinMethodType,
    BuiltinFunctionType
)
from collections import OrderedDict


class Nothing(object):
    __instance = None

    def __new__(cls, *args, **kwargs):
        if Nothing.__instance is None:
            Nothing.__instance = object.__new__(cls, *args, **kwargs)
        return Nothing.__instance

    def __nonzero__(self):
        return False


nothing_inst = Nothing()


def unknowntype(value): return value


def method_type(method):
    raise errors.object_has_no_property(method.__name__)


def unwrapp(arg):
    obj = arg if not isinstance(arg, primitive) else \
        arg.as_complex if hasattr(arg, "as_complex") else arg.as_simple
    return unwrappers.get(type(obj), unknowntype)(obj)

# unwrapp             = lambda arg:     unwrappers.get( type( arg if not isinstance( arg, primitive ) else \
#                                                    arg.as_complex if hasattr( arg, "as_complex" ) else arg.as_simple ),
#                                                    unknowntype )( arg )


def unwrapp_array(args): return [unwrapp(arg) for arg in args]


def unwrapp_dictionary(args): return dict([(unwrapp(key),  unwrapp(args(key))) for key in args])


def unwrapp_kwargs(kwargs): return {key: unwrapp(value) for key, value in kwargs.iteritems()}


def unwrapp_date(value): return datetime(*decode_date(value))


def wrapp(arg): return wrappers.get(type(arg), unknowntype)(arg)


def wrapp_array(args): return array([wrapp(arg) for arg in args])


def wrapp_dict(args): return dictionary({wrapp(key): wrapp(value) for key, value in args.iteritems()})


def wrapp_orddict(args): return dictionary(OrderedDict((wrapp(key), wrapp(value)) for (key, value) in args.iteritems()))


def wrapp_date(value): return date(encode_date(*list(value.timetuple())[:6]))


unwrappers = {
    null: lambda value: None,
    empty: lambda value: None,
    nothing: lambda value: None,
    integer: lambda value: value.as_integer,
    double: lambda value: value.as_double,
    string: lambda value: value.as_string,
    boolean: lambda value: value.as_boolean,
    date: unwrapp_date,
    array: unwrapp_array,
    dictionary: unwrapp_dictionary,
    ordereddictionary: lambda value: OrderedDict([(unwrapp(k), unwrapp(value(k))) for k in value]),
    binary: lambda value: value.as_binary
}

wrappers = {
    long: lambda value: integer(value),
    int: lambda value: integer(value),
    float: lambda value: double(value),
    unicode: lambda value: string(value),
    str: lambda value: string(value),
    NoneType: lambda value: v_empty,
    Nothing: lambda value: v_nothing,
    list: wrapp_array,
    tuple: wrapp_array,
    dict: wrapp_dict,
    OrderedDict: lambda value: ordereddictionary([(wrapp(k), wrapp(v)) for k, v in value.iteritems()]),
    bool: lambda value: boolean(value),
    FunctionType: method_type,
    MethodType: method_type,
    UnboundMethodType: method_type,
    BuiltinFunctionType: method_type,
    BuiltinMethodType: method_type,
    datetime: wrapp_date,
    OrderedDict: wrapp_orddict,
}

# unwrapp             = lambda arg:     unwrappers.get( type( arg.as_simple if not \
#                                    hasattr( arg, "as_complex" ) else \
#                                    arg.as_complex ), unknowntype )( arg )


def convert(method, args, single=False):
    if method == unwrapp_kwargs:
        return method(args)

    if single:
        args = (args, )
    result = method(args)

    if not single:
        return result
    elif type(result) == list:
        return result[0]
    else:
        return result(integer(0))


def convert_input_args(args): return convert(unwrapp_array,     args)


def convert_input_kwargs(kwargs): return convert(unwrapp_kwargs,     kwargs)


def convert_output_arg(arg): return convert(wrapp_array, arg,  True)


def AutoCast(method):
    def wrapper(*args, **kwargs):
        return convert_output_arg(method(*convert_input_args(args),
                                         **convert_input_kwargs(kwargs)
                                         ))
    return wrapper


#######################################
# Cached Property Class
#######################################
class CachedProperty(object):
    def __init__(self, func, autocast=False):
        self.auto_cast = autocast
        self.func = func

    def __get__(self, instance, cls=None):
        result = instance.__dict__[self.func.__name__] = \
            convert_output_arg(self.func(instance)) if self.auto_cast \
            else self.func(instance)

        return result


def AutoCastCachedProperty(func):
    return CachedProperty(func, True)


#######################################
# VScript decorators
#######################################
def v_PropertySimple(func):
    def wrapper(*args, **kwargs):
        if "let" in kwargs:
            return func(value=kwargs["let"], retVal=False, *args)
        elif "set" in kwargs:
            raise errors.object_has_no_property(func.__name__)
        else:
            return func(value=None, retVal=True, *args)
    return wrapper


def v_PropertyComplex(func):
    def wrapper(*args, **kwargs):
        if "set" in kwargs:
            return func(value=kwargs["set"], retVal=False, *args)
        elif "let" in kwargs:
            raise errors.object_has_no_property(func.__name__)
        else:
            return func(value=None, retVal=True, *args)
    return wrapper


def v_PropertyReadOnly(func):
    def wrapper(*args, **kwargs):
        if "let" in kwargs or "set" in kwargs:
            raise errors.object_has_no_property(func.__name__)
        return func(*args, **kwargs)
    return wrapper


#######################################
# Help functions
