def is_string(value): return isinstance(value, basestring)


def is_byte_string(value): return isinstance(value, str)


def is_unicode_string(value): return isinstance(value, unicode)


def is_array(value): return isinstance(value, (list, tuple))


def is_dict(value): return isinstance(value, dict)


def encodeUTF8(value): return value.encode("utf8")


def decodeUTF8(value): return value.decode("utf8")


# Enums
def enum(*sequential, **named):
    enums = dict(zip(sequential, range(len(sequential))), **named)
    enums["enums"] = enums
    return type('Enum', (), enums)

from api_error_code import error_code
from xml.dom.minidom import Document


def api_response_pair(success, value):
	return { "success" if success else "error": value}


class APICallFailedException( Exception ):
	def __init__(self, message, additional_info = None ):
		Exception.__init__( self, message )
		self.info = additional_info


#used for API methods
def _write_response( data ):
	import json
	session[ 'response' ] = json.dumps( data )

def write_response( data = None ):
	_write_response( [ 'success', data ] if data is not None else [ 'success' ] )

def write_error( error_type, error_msg ):
	_write_response( [ 'error', error_type, error_msg ] )

def authenticated(method):
	def wrapper( *args, **kwargs ):
		import ProAdmin
		user = ProAdmin.current_user()
		if user is None:
			write_error(*error_code['errNotLoggedIn'])
		else:
			return method()
	return wrapper

def administrator(method):
    @authenticated
    def wrapper(*args, **kwargs):
        import ProAdmin
        current_user = ProAdmin.current_user()
        if bool(ProAdmin.application().rules(subject=current_user, access='a')):
            return method()
        else:
            write_error(*error_code['errNotNeededRules'])
    return wrapper

def license_confirmed( method ):
	def wrapper( *args, **kwargs ):
		from class_license import License
		if not License().confirmed:
			write_error( *error_code[ 'errNoLicense' ] )
		else:
			return method()
	return wrapper

def error_handler( method ):
	def wrapper( *args, **kwargs ):
		try:
			return method()
		except APICallFailedException, cfex:
			err = error_code[ cfex.message ]
			write_error( err[ 0 ], err[ 1 ] + ( ( ": " + cfex.info ) if cfex.info else "" )  )

		except:
			from vdom_trace import Trace
			write_error( error_code[ 'errScriptError' ][0], Trace.exception_trace() )

	return wrapper

def parse_json( method ):
	def wrapper( *args, **kwargs ):
		import json
		try: data = json.loads( request.arguments.get( 'xml_data' ) )
		except Exception, ex: raise APICallFailedException( "errBadJSONFormat" )
		return method( data, *args, **kwargs )
	return wrapper

def create_success_doc(success_txt=None):
	""" returns an XML document with an <success> base element filled with a specified 'success_text' """

	# create a minidom document
	doc = Document()

	# create <success> base element
	success = doc.createElement("success")
	doc.appendChild(success)

	# set 'success' text
	if success_txt is not None:
		txt = doc.createTextNode(success_txt)
		success.appendChild(txt)

	return doc

def create_error_doc(error_txt):
	""" returns an XML document with an <error> base element filled with a specified 'error_text' """

	# create a minidom document
	doc = Document()

	# create <error> base element
	error = doc.createElement("error")
	doc.appendChild(error)

	# set <error> text
	error_txt = doc.createTextNode(error_txt)
	error.appendChild(error_txt)

	return doc

