class AppinmailAPIError(Exception): pass

class AppinmailClient(object):
	APP_ID = 'f706ee35-6bc3-4aa9-9b68-c182025e5dd3'
	API_ID = '9d29fd1a-ae9b-4b92-8c2f-72c15d18dcf6'

	_instance = None

	class API(object):
		USER = 'appinmail_api'
		PASSWORD_HASH = '2ab4ac3348b06e078442eb5f2e7b4a85'

		def __init__(self, host):
			self.service = VDOMService.connect(host, self.USER, self.PASSWORD_HASH, AppinmailClient.APP_ID)

		def __call__(self, method, args=None):
			try:
				return self.service.call(AppinmailClient.API_ID, method, args)
			except:
				self.service.open_session()
			return self.service.call(AppinmailClient.API_ID, method, args)

	def __init__(self, host=None):
		self.host = host or Config.get('api_host') or Config.get('host') or 'admin.appinmail.io'
		self._api = None

	@property
	def api(self):
		if self._api is None:
			self._api = self.API(self.host)
		return self._api

	def call(self, method, args=None):
		return self.api(method, args)

	def json_call(self, method, args=None):
#		if method == 'auth_token' : raise Exception(1)
		args = args or ''
		args = json.dumps(args)

		resp = self.call(method, args)

		try:
			status, res = json.loads(resp)
			if status == 'error':
				raise AppinmailAPIError(res)
			return res

		except (ValueError, TypeError):
			return resp

	def __getattr__(self, key):
		try:
			return object.__getattr__(self, key)
		except AttributeError:
			pass

		return lambda *args: self.json_call(key, *args)


	@classmethod
	def default(self):
		if self._instance is None:
			self._instance = AppinmailClient()
		return self._instance

	@classmethod
	def reset(self):
		self._instance = None



def create_proadmin_appinmail_user(user, appinmail_guid=None):
	class ProAdminAppinmailUser(user.__class__):
		def __init__(self, proadmin_user, appinmail_guid=None):
			self.proadmin_user = proadmin_user
			self.appinmail_guid = appinmail_guid

		def __getattr__(self, key):
			try:
				return object.__getattr__(self, key)
			except AttributeError:
				pass

			return object.__getattribute__(self.proadmin_user, key)

		def check_password(self, password):
			from md5 import md5

			args = {
				'user_guid' : self.appinmail_guid or self.guid,
				'password_md5' : md5(password).hexdigest()
			}

			appinmail = AppinmailClient.default()
			return appinmail.check_password(args)

	return ProAdminAppinmailUser(user, appinmail_guid)



class SSOClient(object):
	@classmethod
	def is_appinmail_sso(self):
		return Config.get('host') is not None and Config.get('active')


	@classmethod
	def current_protocol(self):
		return Request.protocol()

	@classmethod
	def check_url_protocol(self, url):
		return Url.check_protocol(url)

	@classmethod
	def current_url(self):
		return Url.check_protocol(Request.current_url(), 'https')



	@classmethod
	def sso_url(self, action):
		args = {'continue_url' : self.current_url()}
		host = Config.get('host')

		url = Url.join(host, action)
		url = Url.update_query(url, {'sso' : Utils.serialize(args)})
		return Url.check_protocol(url, 'https')

	@classmethod
	def sso_redirect(self, url):
		response.redirect(url)




	@classmethod
	def sso_request(self, a):
		pass






	@classmethod
	def get_auth_token(self):
		return request.arguments.get('auth_token')

	@classmethod
	def get_proadmin_user(self):
		import ProAdmin
		return ProAdmin.current_user()

	@classmethod
	def set_proadmin_user(self, user_info, save=True):
		import ProAdmin

		users = ProAdmin.application().get_users(email=user_info.get('login'))
		if not users:
			user = ProAdmin.application().create_user(user_info.get('login'))
			user.guid = user_info.get('guid')
		else:
			user = users[0]

		user.notification_email = user_info.get('email')
		user.first_name = user_info.get('first_name')
		user.last_name = user_info.get('last_name')
		if save: user.save()

		ProAdmin.application().add_rule(subject=user, access='a')

		user = create_proadmin_appinmail_user(user, appinmail_guid=user_info.get('guid'))
		ProAdmin.set_user(user)
		session['access_token'] = user_info.get('access_token')


	@classmethod
	def login_auth_token(self, auth_token_key, redirect=True):
		appinmail = AppinmailClient.default()
		user_info = appinmail.auth_token(auth_token_key)

		if user_info:
			self.set_proadmin_user(user_info)
			return True

		return False


	@classmethod
	def success_login(self):
		back_url = request.arguments.get('back_url') or '/home'
		response.redirect(back_url)


	@classmethod
	def login(self):
		if not self.is_appinmail_sso():
			return

		if self.get_proadmin_user():
			return self.success_login()

		# sso_host = request.arguments.get('sso_host')
		# if sso_host:
		# 	sso_host = Utils.deserialize(sso_host)
		# 	if Config.get('host') != sso_host:
		# 		Config.set('host', 'sso_host')
		# 		Config.save()

		auth_token = self.get_auth_token()

		if auth_token and self.login_auth_token(auth_token):
			return self.success_login()

		url = self.sso_url('/login')
		self.sso_redirect(url)

	@classmethod
	def sso_logoff_complete(self):
		status = request.arguments.get('status')
		return bool(status)

	@classmethod
	def logoff(self):
		if not self.is_appinmail_sso():
			return

		import ProAdmin
		ProAdmin.logoff()

		url = self.sso_url('/logoff')
		self.sso_redirect(url)





SSO = SSOClient




def test_promail_clean():
	for db_name in application.databases.get_list().values():
		db = getattr(application.databases, db_name, None)
		if not db: continue

		for table_name in db.get_list():
			db.commit('delete from {0}'.format(table_name))

	from prosuite_app_cleaner import ApplicationCleaner
	cleaner = ApplicationCleaner()
	cleaner.clean_storage = True
	cleaner.clean_vee_engine = True

#	import ProAdmin
#	ProAdmin.scheme().connection.recreate()

	from promail_orm import Mailbox

	mailboxes = list(Mailbox.select())
	for m in mailboxes:
		m.remove()

	response.write('<pre>promail clean - done</pre>')

import json
import base64
import zlib
import urlparse, urllib
import threading



class Local(object):
	_local = threading.local()

	@classmethod
	def get(self, key, default=None):
		return getattr(self._local, key, default)

	@classmethod
	def set(self, key, value):
		self._local.__setattr__(key, value)


def thread_local_cache(clsname=None):
	def decorator(func):
		key = func.__name__
		if clsname: key = str(clsname) + '_' + key

		def wrapper(*args, **kwargs):
			import threading
			value = Local.get(key)
			if value is None:
				value = func(*args, **kwargs)
				Local.set(key, value)
			return value

		return wrapper

	return decorator




class Utils(object):
	@classmethod
	def serialize(self, data):
		jdata = json.dumps(data)
		zdata = zlib.compress(jdata)
		bdata = base64.urlsafe_b64encode(zdata)
		return bdata

	@classmethod
	def deserialize(self, bdata):
		if isinstance(bdata, unicode):
			bdata = bdata.encode('utf8')
		zdata = base64.urlsafe_b64decode(bdata)
		jdata = zlib.decompress(zdata)
		data = json.loads(jdata)
		return data

	@classmethod
	def update_url_query(self, urlstring, data):
		return Url.update_query(urlstring, data)




class Cookies(object):
	@classmethod
	def get(self, name):
		c = response.cookies.get(name)
		return c.value if c else None

	@classmethod
	def set(self, name, value, params={}):
		response.cookies[name] = value

		for key in params.keys():
			v = params[key]
			del params[key]
			params[key.lower()] = v

		if 'max-age' not in params:
			params['max-age'] = 7 * 24 * 3600 * 1000 # 7 days

		for k,v in params.iteritems():
			response.cookies[name][k] = v

	@classmethod
	def delete(self, name):
		response.cookies[name] = ''
		response.cookies[name]['max-age'] = 0




class Request(object):
	@classmethod
	def protocol(self):
		return request.protocol.name.lower()
#		return 'https' if request.environment['SERVER_PORT'] == '443' else 'http'

	@classmethod
	def host(self):
		return request.headers.get('host')

	@classmethod
	def domain(self):
		return '.'.join(self.host().split('.')[1:])

	@classmethod
	def page(self):
		return ''.join(self.query().split('?', 1)[0])

	@classmethod
	def query(self):
		return request.environment.get('REQUEST_URI')

	@classmethod
	def url_parts(self):
		return {
			'protocol' 	: self.protocol(),
			'host' 		: self.host().rstrip('/'),
			'query' 	: self.query().lstrip('/')
		}

	@classmethod
	def current_url(self):
		return '{protocol}://{host}/{query}'.format(**self.url_parts())

	@classmethod
	def get(self, key=None, default=None):
		if key is None:
			data = dict([(k, request.arguments.get(k)) for k in request.arguments])
			return data
		return request.arguments.get(key, default)


	@classmethod
	def remote_ip(self):
		pass


	@classmethod
	def is_e2vdom(self):
		return request.render_type == 'e2vdom'

	@classmethod
	def is_soap(self):
		return request.environment['SCRIPT_NAME'] == '/SOAP'

	@classmethod
	def is_http(self):
		return not self.is_e2vdom() and not self.is_soap()






class Url(object):
	@classmethod
	def current(self):
		return Request.current_url()

	@classmethod
	def join(self, *args):
		args = map(lambda x: x.strip('/'), args)
		return '/'.join(args)

	@classmethod
	def check_protocol(self, url, protocol=None):
		if '://' not in url[:8]:
			protocol = protocol or Request.protocol()
			return protocol + '://' + url

		if not protocol:
			return url

		parts = url.split('://')
		parts[0] = protocol
		return '://'.join(parts)

	@classmethod
	def update_query(self, urlstring, data):
		u = urlparse.urlparse(urlstring)
		query_data = urlparse.parse_qs(u.query)

		delete_args = [k for k,v in data.iteritems() if v is None]
		for key in delete_args:
			del data[key]
			if key in query_data:
				del query_data[key]

		query_data.update(data)
		query_tuples = []

		for key,values in query_data.iteritems():
			if not isinstance(values, list):
				values = [values]

			for value in values:
				query_tuples.append((key, value))

		query = urllib.urlencode(query_tuples)

		u = list(u)
		u[4] = query
		return urlparse.urlunparse(u)

	@classmethod
	def for_page(self, page, args=None):
		parts = Request.url_parts()
		parts['query'] = page.lstrip('/')

		url = '{protocol}://{host}/{query}'.format(**parts)
		if not args: return url

		if isinstance(args, basestring):
			return url + '?' + args.lstrip('?')

		return self.update_query(url, args)



class AppinmailProAdmin(object):
	@classmethod
	def appinmail_user_wrapper(proadmin_user, appinmail_guid=None):
		class ProAdminAppinmailUser(proadmin_user.__class__):
			def __init__(self, proadmin_user, appinmail_guid=None):
				self.proadmin_user = proadmin_user
				self.appinmail_guid = appinmail_guid
				self.access_token = None

			def __getattr__(self, key):
				try:
					return object.__getattr__(self, key)
				except AttributeError:
					pass

				return object.__getattribute__(self.proadmin_user, key)

			def check_password(self, password):
				from md5 import md5

				args = {
					'user_guid' : self.appinmail_guid or self.guid,
					'password_md5' : md5(password).hexdigest()
				}

				appinmail = AppinmailClient.default()
				return appinmail.check_password(args)

		return ProAdminAppinmailUser(proadmin_user, appinmail_guid)

	@classmethod
	def add_application_rule(self, user, right):
		import ProAdmin
		ProAdmin.application().add_rule(user, access=right)

	@classmethod
	def sudo(self, user):
		self.add_application_rule(user, 'a')

	@classmethod
	def current_user(self):
		import ProAdmin
		return ProAdmin.current_user()

	@classmethod
	def create_user(self, user_info):
		import ProAdmin

		users = ProAdmin.application().get_users(email=user_info.get('login'))
		if not users:
			user = ProAdmin.application().create_user(user_info.get('login'))
			user.guid = user_info.get('guid')
		else:
			user = users[0]

		user.notification_email = user_info.get('email')
		user.first_name = user_info.get('first_name')
		user.last_name = user_info.get('last_name')

		user = self.appinmail_user_wrapper(user, appinmail_guid=user_info.get('guid'))
		user.access_token = user_info.get('access_token')

		return user


#		if save: user.save()
#
#		ProAdmin.application().add_rule(subject=user, access='a')
#
#		user = self.create_proadmin_appinmail_user(user, appinmail_guid=user_info.get('guid'))
#		ProAdmin.set_user(user)
#		session['access_token'] = user_info.get('access_token')


	@classmethod
	def set_user(self, user):
		import ProAdmin
		ProAdmin.set_user(user)
		session['access_token'] = user.access_token

