c = CustomEvent.get_custom_event_by_plugin_guid_and_name(self.plugin_guid, self.name) if self.guid != getattr(c, "guid", "") and c: session['error'] = [(lang["plugins.custom_event.name_exist.error"], )] raise RuntimeError()
def save(self): self._check_on_name() return self.__update() if self.id else self.__insert()
def __insert(self): self.guid = str(uuid4()) if self.guid is None else self.guid #raise Exception(self.guid, self.name) self.id = Database.macrosdb().commit( """INSERT INTO custom_event (name, guid, plugin_guid) VALUES ({0},{0},{0})""".format(FMT_CHR), (self.name, self.guid, self.plugin_guid))
self.register() return self
