from class_db import Database from object_cache import Object_Cache from uuid import uuid4 from VEE_prosuite_events import VEE_OnPluginUninstall import base64 from VEE_resources import ResourceFolderManager
FMT_CHR = "?"
class Plugins: def __init__(self): self.id = None self.name = None self.description = None self.author = None self.picture = None self.guid = None self.zindex = None self.version = None self.protected = None self.__macros = [] self.__timer = [] self.__custom_event = []
def __fill_from_row(self, row): self.id = row[0] self.name = row[1] self.description = row[2] self.author = row[3] self.picture = row[4] self.guid = row[5] self.zindex = row[6] self.version = row[7] self.protected = row[8] return self
@classmethod def get_all(self): db_rows = Database.macrosdb().fetch_all(""" SELECT plugin.id, plugin.name, plugin.description, plugin.author, plugin.picture, plugin.guid, plugin.zindex, plugin.version, plugin.protected FROM `plugin`""", )
return [self().__fill_from_row(row) for row in db_rows]
def get_macros(self): from class_macro import Macros self.__macros = Macros.get_macros_by_plugin_guid(self.guid) return self.__macros
def get_timer(self): from class_timer import Timer self.__timer = Timer.get_timer_by_plugin_guid(self.guid) return self.__timer
def get_custom_event(self): from class_custom_event import CustomEvent self.__custom_event = CustomEvent.get_custom_event_by_plugin_guid(self.guid) return self.__custom_event
@classmethod def get_by_id(self, id): db_row = Database.macrosdb().fetch_one(""" SELECT plugin.id, plugin.name, plugin.description, plugin.author, plugin.picture, plugin.guid, plugin.zindex, plugin.version, plugin.protected FROM `plugin` WHERE id={}""".format(FMT_CHR), (id,)) return self().__fill_from_row(db_row) if db_row else None
@classmethod def get_by_guid(self, guid): if plugin_cache.loaded(): return plugin_cache.get_by_guid(guid) else: db_row = Database.macrosdb().fetch_one(""" SELECT plugin.id, plugin.name, plugin.description, plugin.author, plugin.picture, plugin.guid, plugin.zindex, plugin.version, plugin.protected FROM `plugin` WHERE guid={}""".format(FMT_CHR), (guid,)) return self().__fill_from_row(db_row) if db_row else None
def save(self): return self.__update() if self.id else self.__insert()
def __insert(self): self.id = Database.macrosdb().commit( """INSERT INTO plugin (name, description, author, picture, guid, zindex, version, protected) VALUES ({0},{0},{0},{0},{0},{0},{0},{0})""".format(FMT_CHR), (self.name, self.description, self.author, self.picture, self.guid, self.zindex, self.version,self.protected) ) return self
def __update(self): Database.macrosdb().commit( """UPDATE plugin SET name={0}, description={0}, author={0}, picture={0}, guid={0}, zindex={0}, version={0}, protected={0} WHERE id={0}""".format(FMT_CHR), (self.name, self.description, self.author, self.picture, self.guid, self.zindex, self.version, self.protected, self.id) ) return self
def delete(self, keep_storage=False): VEE_OnPluginUninstall(self, self.guid).activate() macros_list = self.get_macros() for macro in macros_list: macro.delete() timer_list = self.get_timer() for timer in timer_list: timer.delete() custom_event_list = self.get_custom_event() for cevent in custom_event_list: cevent.delete()
Database.macrosdb().commit("""DELETE FROM plugin WHERE id={}""".format(FMT_CHR), (self.id,)) if not keep_storage: Database.macrosdb().commit("""DELETE FROM kv_macro_storage WHERE namespace={}""".format(FMT_CHR), (self.guid,))
def export(self): from StringIO import StringIO import base64
from class_xml_plugin import XMLPlugin xml_plugin = XMLPlugin()
res_manager = ResourceFolderManager(self.guid) xml_plugin.picture = '' if not self.picture or not res_manager.exists(self.picture) else self.picture xml_plugin.description = self.description if self.description else '' xml_plugin.name = self.name if self.name else '' xml_plugin.version = self.version if self.version else '' xml_plugin.author = self.author if self.author else '' xml_plugin.guid = self.guid
timers = self.get_timer() for timer in timers: from class_timer import Timer t = Timer.get_by_id(timer.id) xml_plugin.append_child(t.get_xmlnode())
custom_events = self.get_custom_event() for cevent in custom_events: from class_custom_event import CustomEvent ce = CustomEvent.get_by_id(cevent.id) xml_plugin.append_child(ce.get_xmlnode())
from VEE_sqlite3 import DatabaseManager
db_list = DatabaseManager(self.guid).databaselist if db_list: for db in db_list: from class_plugin_db import PluginDB plugin_db = PluginDB(db, DatabaseManager(self.guid).export_db(db)) xml_plugin.append_child(plugin_db.get_xmlnode())
res_list = ResourceFolderManager(self.guid).resourcelist if res_list: for res in res_list: from class_resource import Resource resource = Resource(res, ResourceFolderManager(self.guid).export_res(res)) xml_plugin.append_child(resource.get_xmlnode())
macros = self.get_macros() if macros: for macro in macros: from class_macro import Macros m = Macros.get_by_id(macro.id) xml_plugin.append_child(m.get_xmlnode()) from class_xml_kv_macro_storage import XMLKvMacroStorage kv_macro_storage = Database.macrosdb().fetch_all( "SELECT `key`, value FROM kv_macro_storage WHERE namespace={}".format(FMT_CHR), (self.guid,) )
if kv_macro_storage: for _ in kv_macro_storage: xml = XMLKvMacroStorage() xml.key = _[0] xml.value = _[1] xml_plugin.append_child(xml)
outp = StringIO() outp.write(xml_plugin.toprettyxml().encode('utf8')) return outp
def fill_from_xml(self, xml): self.name = xml.name self.description = xml.description self.guid = xml.guid self.version = xml.version self.author = xml.author self.protected = xml.protected self.picture = xml.picture self.save()
def get_md5(self): import md5 return md5.md5(self.export().getvalue()).hexdigest()
@classmethod def isBase64(cls, s): try: return base64.b64encode(base64.b64decode(s)) == s except Exception: return False
@classmethod def install_plugin(self, plugin_xml): from xml.dom.minidom import parseString
from class_macro import Macros from class_plugins import Plugins from class_timer import Timer from class_custom_event import CustomEvent from class_xml_macro import XMLMacros from class_xml_timer import XMLTimer from class_xml_plugin import XMLPlugin from class_xml_plugin_db import XMLPluginDB from class_xml_resource import XMLResource from class_xml_custom_event import XMLCustomEvent from VEE_resources import create_plugin_dir, ResourceFolderManager from VEE_sqlite3 import DatabaseManager
dom = parseString(plugin_xml) node = XMLPlugin(dom)
plugin = Plugins()
plugin.name = node.name plugin.description = node.description plugin.guid = node.guid if not plugin.guid: raise ValueError("Invalid plugin's xml. GUID value cannot be empty.") plugin.version = node.version plugin.author = node.author
exist_plugin = Plugins.get_by_guid(plugin.guid) if exist_plugin: if exist_plugin.version > plugin.version: return else: if exist_plugin.picture: application.storage.delete(exist_plugin.picture) exist_plugin.delete()
plugin.picture = node.picture
plugin.save()
create_plugin_dir(plugin.guid) dbManager = DatabaseManager(plugin.guid) resManager = ResourceFolderManager(plugin.guid)
if node.picture and self.isBase64(node.picture): plugin.picture = plugin.name + ".png" resManager.import_res(plugin.picture, base64.b64decode(node.picture)) else: plugin.picture = node.picture
plugin.save()
for child in node.childs: if child.tag == "timer": child = XMLTimer(child) if child.name: timer = Timer() timer.fill_from_xml(child, node.guid)
elif child.tag == "custom_event": child = XMLCustomEvent(child) if child.name: custom_event = CustomEvent() custom_event.fill_from_xml(child, node.guid)
elif child.tag == "macro": child = XMLMacros(child) if child.name and child.source: macros = Macros() macros.fill_from_xml(child, node.guid)
elif child.tag == "database": child = XMLPluginDB(child) if child.name: dbManager.import_db(child.name, base64.b64decode(child.db_source))
elif child.tag == "resource": child = XMLResource(child) if child.name: resManager.import_res(child.name, base64.b64decode(child.res_source)) return plugin
class Plugin_Cache(Object_Cache): def __init__(self): Object_Cache.__init__(self, Plugins)
def get_by_guid(self, guid): r = self.filter(lambda x: x.guid == guid) return r[0] if r else None
plugin_cache = Plugin_Cache()

from class_db import Database from VEE_core import engine from uuid import uuid4
FMT_CHR = "?"
class Timer: def __init__(self): self.id = None self.name = None self.period = None self.guid = None self.plugin_guid = None
def __fill_from_row(self, row): self.id = row[0] self.name = row[1] self.period = row[2] self.guid = row[3] self.plugin_guid = row[4]
return self
@classmethod def get_by_id(self, id): db_row = Database.macrosdb().fetch_one(""" SELECT timer.id, timer.name, timer.period, timer.guid, timer.plugin_guid FROM `timer` WHERE timer.id={}""".format(FMT_CHR), (id,)) return self().__fill_from_row(db_row) if db_row else None
@classmethod def register_timer_by_guid(self, guid): timer = self.get_timer_by_guid(guid) if timer: timer.register()
def __internal_name(self): return u"{0}:{1}".format(self.plugin_guid, self.name).encode("utf-8").lower()
def register(self): engine.add_timer(self.__internal_name(), self.period, self.guid)
def unregister(self): engine.delete_timer(self.__internal_name())
def update_timer(self): engine.update_timer(self.__internal_name(), self.period, self.guid)
@classmethod def get_timer_by_plugin_guid(self, plugin_guid): db_rows = Database.macrosdb().fetch_all(""" SELECT timer.id, timer.name, timer.period, timer.guid, timer.plugin_guid FROM `timer` WHERE timer.plugin_guid={}""".format(FMT_CHR), (plugin_guid,)) return [self().__fill_from_row(row) for row in db_rows]
@classmethod def get_timer_by_plugin_guid_and_name(self, plugin_guid, name): db_row = Database.macrosdb().fetch_one(""" SELECT timer.id, timer.name, timer.period, timer.guid, timer.plugin_guid FROM `timer` WHERE timer.plugin_guid={0} and timer.name={0}""".format(FMT_CHR), (plugin_guid, name,)) return self().__fill_from_row(db_row) if db_row else None
@classmethod def get_timer_by_guid(self, guid): db_row = Database.macrosdb().fetch_one(""" SELECT timer.id, timer.name, timer.period, timer.guid, timer.plugin_guid FROM `timer` WHERE timer.guid={}""".format(FMT_CHR), (guid,)) return self().__fill_from_row(db_row) if db_row else None
def _check_on_name(self): from prosuite_localization import get_localization lang = get_localization()
if not self.name: session['error'] = [(lang["plugins.timer.name.error"], )] raise RuntimeError()
t = Timer.get_timer_by_plugin_guid_and_name(self.plugin_guid, self.name) if self.guid != getattr(t, "guid", "") and t: session['error'] = [(lang["plugins.timer.name_exist.error"], )] raise RuntimeError()
def save(self): self._check_on_name() return self.__update() if self.id else self.__insert()
def __insert(self): self.guid = str(uuid4()) if self.guid is None else self.guid self.id = Database.macrosdb().commit( """INSERT INTO timer (name, period, guid, plugin_guid) VALUES ({0},{0},{0},{0})""".format(FMT_CHR), (self.name, self.period, self.guid, self.plugin_guid) ) self.register() return self
def __update(self): Database.macrosdb().commit( """UPDATE timer SET name={0}, period={0}, guid={0}, plugin_guid={0} WHERE id={0}""".format(FMT_CHR), (self.name, self.period, self.guid, self.plugin_guid, self.id) ) self.update_timer() return self
def delete(self): from class_macro import Macros
macros_list = Macros.get_macros_by_timer_guid(self.guid) for macros in macros_list: macros.class_name = "NULL" macros.timer_guid = "NULL" macros.is_button_macros = "1" macros.on_board = "0" macros.save()
self.unregister() Database.macrosdb().commit("""DELETE FROM timer WHERE id={}""".format(FMT_CHR), (self.id,))
