class Icon(object):
	""" class for Icon routines in ProAdmin
	"""
	def __init__(self, res_name=""):
		self._resource_url = res_name

	@classmethod
	def on_resource(cls, res_name):
		""" create Icon object from resource image
		"""
		url = "/%s.res" % res_name
		return Icon(url) 

