class BaseProAdminScheme(object):
	""" Base class for ProAdmin scheme
	"""
	def __init__(self, guid=None, objects_connection=None, subjects_connection=None):
		# connections
		self._objects_conn	= objects_connection
		self._subjects_conn	= subjects_connection

		# main attributes
		self.guid = guid
		self.name = ''
		self.icon = None
		self.type = ''
		self.config = None

		# application instance
		self._application = None

		# specific scheme oprtions
		self.options = {}

		# acl object types
		self._acl_object_types = []
		self.prepare_synchronize = None

	@property
	def application(self):
		return self._application
		
	def get_application(self):
		""" obsolete. need for compatibility
		"""
		return self.application

	@property
	def objects_connection(self):
		return self._objects_conn
		
	@property	
	def connection(self):
		""" obsolete. need for compatibility
		"""
		return self.objects_connection
		
	def get_connection(self):
		""" obsolete. need for compatibility
		"""
		return self.objects_connection

	@property
	def subjects_connection(self):
		return self._subjects_conn

	def userconnection(self):
		""" obsolete. need for compatibility
		"""
		return self.subjects_connection

	def is_remote(self):
		return self.type == 'remote'

	def synchronize(self):
		pass

	def register(self, make_default=None):
		""" register this scheme
		"""
		from proadmin_acl_object import ACLObject
		import ProAdmin
		self._application = ACLObject.application(self.name, self.guid, self)
		ProAdmin.register_scheme(self)

	def unregister(self):
		""" unregister this scheme
		"""
		import ProAdmin
		ProAdmin.unregister_default_scheme()

	def delete(self):
		""" delete this scheme from LDAP
		"""
		import ProAdmin

		self.application.delete()
		ProAdmin.delete_scheme(self)

	def set_information(self, name="", icon=None):
		""" set information about scheme: name and icon
		"""
		self.name = name
		self.icon = icon

	def set_option(self, key, value):
		""" set value for option specific for application
		"""
		self.options[key] = value

	def get_option(self, key, default_value=None):
		"""
		"""
		return self.options.get(key, default_value)

	def set_api_guid(self, api_guid):
		""" set api guid
		"""
		self.set_option('api_guid', api_guid)

	def get_registered_applications(self):
		""" get applications info that can connecting via API from this applicatino
		"""
		import ProAdmin
		
		app_info = {
			'name': self.name,
			'guid': self.guid,
			'ip': '127.0.0.1',
			'api_guid': self.get_option('api_guid'),
			'hosts': ProAdmin.hosts(),
		}

		return { self.name : app_info }

	def get_registred_applications(self):
		""" obsolete. need for compatibility
		"""
		return self.get_registered_applications()

	def get_application_icon_url(self):
		""" return resource URL to application icon
		"""
		return self.icon.get_resource_url()

	def add_aclobjecttype(self, object_type):
		if not (object_type in self._acl_object_types):
			self._acl_object_types.append(object_type)

	def get_aclobjecttypes(self):
		return self._acl_object_types

	application_icon_url = property(get_application_icon_url)

	def create_root_user(self):
		pass
		
	def create_admins_group(self):
		pass

	def create_user(self):
		pass

	def create_group(self):
		pass

	def get_users(self, email='', guid=''):
		pass

	def get_groups(self, guid='', name='', user=None):
		pass

	def check_password(self, user, password):
		return False

try:
    from model_aux_db_settings import Settings
    DB_NAME = Settings.db_proadmin
except ImportError:
    DB_NAME = 'default_proadmin'


class ProadminSchema(object):
    _create = 'CREATE SCHEMA {} CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;'.format(DB_NAME)
    _drop = 'DROP SCHEMA IF EXISTS {}'.format(DB_NAME)
    _exist = 'SELECT SCHEMA_NAME FROM information_schema.SCHEMATA WHERE SCHEMA_NAME = "{}";'.format(DB_NAME)

    @classmethod
    def create(cls, cursor):
        cursor.execute(cls._create)

    @classmethod
    def drop(cls, cursor):
        cursor.execute(cls._drop)

    @classmethod
    def exist(cls, cursor):
        cursor.execute(cls._exist)


class BaseTable(object):
    _table = ''

    @classmethod
    def create(cls, cursor):
        cursor.execute(cls._table)
        for idx in filter(lambda x: x.startswith('_index'), cls.__dict__.keys()):
            cursor.execute(getattr(cls, idx))


class ProadminAclobject(BaseTable):
    _table = '''
    create table {0}.aclobject
    (
        guid varchar(40) not null
            primary key,
        object_type varchar(255) not null,
        parent_guid varchar(40) null,
        name text not null,
        is_dirty int not null,
        constraint aclobject_ibfk_1
            foreign key (parent_guid) references {0}.aclobject (guid)
                on delete cascade
    );
    '''.format(DB_NAME)
    _index = '''
    create index aclobject_parent_guid_id
        on {0}.aclobject (parent_guid);
    '''.format(DB_NAME)


class ProadminUser(BaseTable):
    _table = '''
    create table {0}.proadmin_user
    (
        guid varchar(40) not null
            primary key,
        email varchar(255) not null,
        last_name varchar(255) not null,
        first_name varchar(255) not null,
        password_hash varchar(255) not null,
        phone varchar(255) not null,
        notification_email varchar(255) not null,
        cell_phone varchar(255) not null,
        country varchar(255) not null,
        keywords_string text not null,
        options_string text not null
    );
    '''.format(DB_NAME)


class ProadminGroup(BaseTable):
    _table = '''
    create table {0}.proadmin_group
    (
        guid varchar(40) not null
            primary key,
        name text not null
    );
    '''.format(DB_NAME)


class ProadminUserInGroup(BaseTable):
    _table = '''
    create table {0}.user_in_group
    (
        user_guid varchar(40) not null,
        group_guid varchar(40) not null,
        primary key (user_guid, group_guid),
        constraint user_in_group_ibfk_1
            foreign key (user_guid) references {0}.proadmin_user (guid),
        constraint user_in_group_ibfk_2
            foreign key (group_guid) references {0}.proadmin_group (guid)
    );
    '''.format(DB_NAME)
    _index_1 = '''
    create index user_in_group_group_guid_id
        on {0}.user_in_group (group_guid);
    '''.format(DB_NAME)
    _index_2 = '''
    create index user_in_group_user_guid_id
        on {0}.user_in_group (user_guid);
    '''.format(DB_NAME)


class ProadminRule(BaseTable):
    _table = '''
    create table {0}.rule
    (
        subject_guid varchar(40) not null,
        aclobject_guid varchar(40) not null,
        access varchar(30) not null,
        constraint rule_ibfk_1
            foreign key (aclobject_guid) references {0}.aclobject (guid)
                on delete cascade
    );
    '''.format(DB_NAME)
    _index_1 = '''
    create index rule_subject_guid_id
        on {0}.rule (subject_guid);
    '''.format(DB_NAME)
    _index_2 = '''
    create index rule_aclobject_guid_id
        on {0}.rule (aclobject_guid);
    '''.format(DB_NAME)


class ProadminDatabaseVersion(BaseTable):
    _table = '''
    create table {0}.database_version
    (
        guid varchar(40) not null
            primary key,
        version int not null
    );
    '''.format(DB_NAME)


