import uuid
import base64
import json
from md5 import md5
from proadmin_subject import User
import ProAdmin


try:
    from model_aux_db_settings import Settings
    FMT_CHAR = Settings.format_char()
except ImportError:
    FMT_CHAR = '?'

try:
    from app_settings import settings
except:
    pass

_pack_scheme = ['guid',
    'email',
    'first_name',
    'last_name',
    'phone',
    'password_hash',
    'notification_email',
    'cell_phone',
    'country',
    'keywords_string',
    'options_string',
]


class DbUser(User):
    @classmethod
    def get_users(cls, scheme, email=None, guid=None):
        global _pack_scheme

        filter_dict = {}
        if email: filter_dict["email"] = email
        if guid: filter_dict["guid"] = guid

        raw_result = scheme.connection.select_cmd('proadmin_user', _pack_scheme, filter_dict)
        return [DbUser(scheme).init_from_db_tuple(db_tuple) for db_tuple in raw_result]

    @classmethod
    def get_in_group(self, scheme, group):
        raw_result = scheme.connection.fetch_all(
            '''SELECT {}
            FROM proadmin_user
            JOIN user_in_group ON user_guid = guid
            WHERE group_guid = {} '''.format(', '.join(_pack_scheme), FMT_CHAR), [group.guid])

        return [DbUser(scheme).init_from_db_tuple(db_tuple) for db_tuple in raw_result]

    @classmethod
    def get_user_by_appinmail_email(self, scheme, appinmail_email=None):

        ALL_USERS = self.get_users(scheme)
        HAS_AIM_KEY = lambda user: user.options.get("AIM", None)
        HAS_REQUIRED_EMAIL = lambda user: user.options.get("AIM", None) == appinmail_email

        final_user = [user for user in ALL_USERS if HAS_AIM_KEY(user) and HAS_REQUIRED_EMAIL(user)]

        return final_user[0] if final_user != [] else None

    @classmethod
    def has_users_attached_to_appinmail(self, scheme):
        ALL_USERS = self.get_users(scheme)
        HAS_AIM_KEY = lambda user: user.options.get("AIM", None)
        for user in ALL_USERS:
        	if HAS_AIM_KEY(user):
        		return True
        
        return False
    
    @classmethod
    def get_users_attached_to_appinmail(self, scheme):
        ALL_USERS = self.get_users(scheme)
        HAS_AIM_KEY = lambda user: user.options.get("AIM", None)
        users = [user for user in ALL_USERS if HAS_AIM_KEY(user)]

        return users[0] if users != [] else None

    def __init__(self, scheme):
        User.__init__(self, scheme)
        self.connection = scheme.connection

        self._guid = str(uuid.uuid4())
        self.keywords_string = ''

        self.options_string = ''
        self.__options = None

    def init_from_db_tuple(self, db_tuple):
        global _pack_scheme
        for k, v in zip(_pack_scheme, db_tuple):
            setattr(self, k, v)
        return self

    def _packed_list(self, pscheme=None):
        global _pack_scheme
        if not pscheme:
            pscheme = _pack_scheme
        return [getattr(self, attr) for attr in pscheme]

    def _packed_dict(self, pscheme=None):
        global _pack_scheme
        if not pscheme:
            pscheme = _pack_scheme
        return dict([(attr, getattr(self, attr)) for attr in pscheme])

    @property
    def exists(self):
        global _pack_scheme
        return len(self.connection.select_cmd('proadmin_user', _pack_scheme, {'guid': self.guid})) > 0

    def save(self):
        global _pack_scheme

        self.options_string = json.dumps(self.options)

        exists = self.exists

        if exists:
            self.connection.update_cmd('proadmin_user', self._packed_dict(), {'guid': self.guid})
        else:
            self.connection.insert_cmd('proadmin_user', _pack_scheme, self._packed_list())
        return self

    def force_delete(self, force=True):
        self.connection.delete_cmd('user_in_group', {"user_guid": self.guid})
        self.connection.delete_cmd('rule', {"subject_guid": self.guid})
        self.connection.delete_cmd('proadmin_user', {"guid": self.guid})

    def get_groups(self):
        from proadmin_db_group import DbGroup
        if not hasattr(self, "__groups"):
            self.__groups = User.get_groups(self) + DbGroup.get_user_groups(self.scheme, self)
        return self.__groups

    def _get_hash(self, value):
        return base64.b64encode(md5(value.encode('utf8') + self.guid).digest())

    def remove_group(self, group):
        group.remove_user(self)

    def add_group(self, group):
        group.add_user(self)

    @property
    def password_hash(self):
        return self._password

    @password_hash.setter
    def password_hash(self, value):
        self._password = value

    def set_password_hash(self, value):
        self.password_hash = value

    @property
    def password(self):
        return self._password

    @password.setter
    def password(self, value):
        if not value:
            return
        self._password = self._get_hash(value)

    def check_local_password(self, password):
        return self.password == self._get_hash(password)

    @property
    def keywords(self):
        return self.keywords_string.split(',')

    @keywords.setter
    def keywords(self, value):
        self.keywords_string = ",".join(value)

    @property
    def options(self):
        if self.__options is None:
            self.__options = json.loads(self.options_string) if self.options_string else {}
        return self.__options

    @options.setter
    def options(self, value):
        self.__options = value

    def set_reset_password_key(self):
        options = json.loads(self.options_string)
        key = uuid.uuid4()
        options['key'] = str(key)
        self.options_string = json.dumps(options)
        self.save()
        return key

    def reset_password(self, new_password, key):
        options = json.loads(self.options_string)
        if (options.get('key') == key) and key is not None:
            options.pop('key')
            self.password = new_password
            self.options_string = json.dumps(options)
            self.save()
            return True
        return False

    def set_jwt(self):
        import jwt
        from datetime import datetime, timedelta
        import pytz

        token_uuid = str(uuid.uuid4())
        
        exp = (datetime.now() + timedelta(settings.JWT_EXP_DAYS) - datetime(1970, 1, 1)).total_seconds()
        token = jwt.encode({'token_uuid': token_uuid, 'user': self.guid, 'exp': exp}, settings.JWT_KEY, algorithm='HS256')
        
        options = json.loads(self.options_string)
        options['token_uuid'] = token_uuid
        self.options_string = json.dumps(options)
        self.save()
        
        return token

    def check_jwt(self, token):
        import jwt

        options = json.loads(self.options_string)
        token_uuid = options.get('token_uuid', False)
        decoded = jwt.decode(token, settings.JWT_KEY, algorithms=['HS256'])
        if (decoded.get('token_uuid') == token_uuid) and token_uuid:
            return
        else:
            raise ProAdmin.ProAdminJWTNotValid()

    def __repr__(self):
        return str(self.email)

    def is_admin(self):
        if ProAdmin.application().rules(self, 'a'):
            return True
        return False

class NotAdminApplicationError(Exception):
	def __init__(self, message=None):
		if not message: message = 'Current user is not an administrator'
		Exception.__init__(self, message)


class NoCurrentUserApplicationError(Exception):
	def __init__(self, message=None):
		if not message: message = 'Session is expired: no logged in user in session'
		Exception.__init__(self, message)


class RemoteApplicationDisconnected(Exception):
	def __init__(self, message=None):
		if not message: message = 'Application disconnected. ID : %s'
		Exception.__init__(self, message)


class ProAdminRegisterSchemeError(Exception):
	def __init__(self, message=None):
		if not message: message = 'Application scheme is not registred in ProAdmin'
		Exception.__init__(self, message)


class ProAdminLoginError(Exception):
	def __init__(self, message=None):
		if not message: message = 'Invalid user name or password'
		Exception.__init__(self, message)


class ProAdminEmptyPasswordError(ProAdminLoginError):
	def __init__(self, message=None):
		if not message: message = 'Password must not be empty'
		Exception.__init__(self, message)


class ProAdminEmptyLoginError(ProAdminLoginError):
	def __init__(self, message=None):
		if not message: message = 'Login must not be empty'
		Exception.__init__(self, message)


