ServerLoginHelper = SSOServer

from abc import abstractmethod
from md5 import md5

import copy
import cgi
import re
import base64

import uuid

import ProAdmin


# ----------------------------------------------------------------
#		Subject
# ----------------------------------------------------------------

class Subject( object ):
	""" Base class for subject structures
	"""
	def __init__( self, scheme ):
		self._guid = ''
		self.scheme = scheme


	def fill_guid_list( self ):
		pass


	def get_guid_list( self ):
		return [ self.guid ]


	@abstractmethod
	def save( self ):
		""" save subject changes
		"""
		return self


	def delete( self, force=False ):
		""" delete subject
		"""
		self.force_delete( force=force )


	@abstractmethod
	def force_delete( self, force=True ):
		raise Exception('Subclass resposibility')


	@classmethod
	def _escape( self, value ):
		""" escape string values
		"""
		if not value:
			value = ''

		return cgi.escape( value )


	@classmethod
	def _modify_dn( self, dn ):
		""" This method get attribute value from dn string.
			Example: dn = 'Cn=Users'. In this case the method returnes 'Users'. ###
		"""
		if type( dn ) == list:
			dn = dn[0]

		dn = self._escape( dn ).split( '=' )

		if len( dn ) == 1:
			return dn[0]

		result = ''
		dn = dn[1:]
		for i in dn:
			result += i

		return result


	@classmethod
	def _scalar( self, value ):
		if not value: value = ''
		if type( value ) == list:
			return self._scalar( value[0] )

		return value.strip()


	def is_admin( self ):
		""" get answer for subject is admin: user-root, group-admin
		"""
		return False

	def is_user( self ):
		""" get answer that subject is user
		"""
		return False

	def is_group( self ):
		""" get answer that subject is group
		"""
		return False


	def is_persistent( self ):
		""" get answer that subject persistent or non persistent
		"""
		pass


	@property
	def guid( self ):
		""" return guid of subject
		"""
		return self._guid

	@guid.setter
	def guid( self, value ):
		""" set guid for subject
		"""
		self._guid = self._scalar( value )


	def __hash__( self ):
		return hash( self.guid )


	def _is_addled_instance( self ):
		""" check, maybe this instance is addled
		"""
		try:
			import ProAdmin
		except ImportError:
			return True

		return False






# ----------------------------------------------------------------
#		User
# ----------------------------------------------------------------

class User( Subject ):
	""" Base class for users
	"""
	def __init__( self, scheme ):
		Subject.__init__( self, scheme )

		# addition attributes
		self._email 		= ''
		self._phone 		= ''
		self._first_name 	= ''
		self._last_name 	= ''
		self._password 		= ''

		self._notification_email = ''
		self._cell_phone	= ''
		self._country		= ''
		self._keywords		= ''

		self._options 		= {}

		# save user-group relationship
		self.group_guid_list = []


	def is_user( self ):
		return True

	def is_group( self ):
		return False


	@property
	def email( self ):
		return self._email

	@email.setter
	def email( self, value ):
		self._email = self._scalar( value )


	@property
	def phone( self ):
		return self._phone

	@phone.setter
	def phone( self, value ):
		self._phone = self._scalar( value )


	@property
	def first_name( self ):
		return self._first_name

	@first_name.setter
	def first_name( self, value ):
		self._first_name = self._scalar( value )


	@property
	def last_name( self ):
		return self._last_name

	@last_name.setter
	def last_name( self, value ):
		self._last_name = self._scalar( value )


	@property
	def name( self ):
		name = '%s %s' % (self.first_name, self.last_name,)
		name = name.strip()
		name = name if name else self.email
		return name


	@property
	def password( self ):
		return self._password

	@password.setter
	def password(self, value ):
		self._password = value



	@property
	def notification_email( self ):
		return self._notification_email

	@notification_email.setter
	def notification_email( self, value ):
		self._notification_email = value


	@property
	def cell_phone( self ):
		return self._cell_phone

	@cell_phone.setter
	def cell_phone( self, value ):
		self._cell_phone = value


	@property
	def country( self ):
		return self._country

	@country.setter
	def country( self, value ):
		self._country = value


	@property
	def keywords( self ):
		return self._keywords

	@keywords.setter
	def keywords( self, value ):
		self._keywords = value
	

	@property
	def options(self):
		return self._options

	@options.setter
	def options(self, value):
		self._options = value




	def get_groups( self ):
		""" return list of groups which contains user
		"""
		return [ Everyone( self.scheme ) ]


	@classmethod
	def get_users( self, scheme, email, guid ):
		return []


	def get_name( self ):
		""" OBSOLETE. need for capability.
		"""
		return self.name


	def check_local_password( self, password ):
		return False

	def get_guid_list( self ):
		groups = self.get_groups()
		return [ group.guid for group in groups ] + [ self.guid ]


	def __pack( self ):
		attributes = {
			'guid'					: self.guid,
			'email'					: self.email,
			'first_name'			: self.first_name,
			'last_name'				: self.last_name,
			'phone'					: self.phone,
			'password'				: self.password,
			'notification_email'	: self.notification_email,
			'cell_phone'			: self.cell_phone,
			'country'				: self.country,
			'keywords'				: self.keywords,
			'options' 				: self.options,
			'root'					: self.is_root(),
		}

		return attributes
	
	def _pack(self):
	    return self.__pack()

	def is_root(self):
		return False


	def __eq__( self, other ):
		if not other: return False
		if not other.is_user(): return False
		return self.__pack() == other.__pack()



	def check_password( self, password ):
		"""
		"""
		return self.scheme.check_password( self, password )

	def set_password_hash(self, hash_p):
		raise Exception("should be implemented in subclass")

