from uuid import uuid4


def safe_str(input_str, maxlen=30, text_only=False, hint=""):
	TEMPLATE = u"""
		<a title='%(orig)s'>%(short)s<a>
	"""
	short_str = input_str if len(input_str) < maxlen else u"%s..."%input_str[:maxlen]
	if text_only:
		result = short_str
	else:
		result = TEMPLATE % {
			"orig" : input_str if not hint else hint,
			"short" : short_str
		}

	return result

def limit_length( input_str, length_limit ):
	return input_str if len( input_str ) < length_limit else "%s..."%input_str[:length_limit]

def session_put( key, value ):
	if value:
		session_key = request.shared_variables[ key ]
		if not session_key:
			session_key = str( uuid4() )
		session[ session_key ] = value
		response.shared_variables[ key ] = session_key
	else:
		session_key = request.shared_variables[ key ]
		if session.get( session_key ):
			del session[ session_key ]
		del response.shared_variables[ key ]


def session_get( key ):
	if request.shared_variables[ key ] is None:
		return None
	return session.get( request.shared_variables[ key ] )


def current_page_name(e2vdom=False):
    from managers import request_manager
    env = request_manager.get_request().environment().environment()
    if e2vdom:
        result = env.get("HTTP_REFERER", "/home")
        result = result.split("/")[-1].split("?")[0].split(".")[0]
    else:
        result = env.get("SCRIPT_NAME", "/home")
        result = result.split("/")[1].split(".")[0]
    return result

def get_env_argument(key):
	from managers import request_manager
	env = request_manager.get_request().environment().environment()
	result = env[key] if key in env else ""
	return result

def get_logout_back_url():
    result = get_env_argument("REQUEST_URI").replace("%26", '&')
    return "/" + current_page_name(True) if result == "/e2vdom.py" else result

def convert_bytes( bytes ):
	bytes = float( bytes )
	if bytes >= 1099511627776:
		terabytes = bytes / 1099511627776
		size = '%.2fTb' % terabytes
	elif bytes >= 1073741824:
		gigabytes = bytes / 1073741824
		size = '%.2fGb' % gigabytes
	elif bytes >= 1048576:
		megabytes = bytes / 1048576
		size = '%.2fMb' % megabytes
	elif bytes >= 1024:
		kilobytes = bytes / 1024
		size = '%.2fKb' % kilobytes
	else:
		size = '%.2fb' % bytes
	return size

import sys, traceback
import cProfile, StringIO, pstats

from datetime import datetime, timedelta


def _pre(*args):
	args = map(str, args)
	text = ' '.join(args)
	text = text.replace('<', '&lt;').replace('>', '&gt;')
	return '<pre style="white-space: pre-wrap;">\n{0}\n</pre>'.format(text)

def p(*args, **kwargs):
	s = _pre(*args)
	response.write(s, kwargs.get('cont'))

def r(*args):
	s = _pre(*args)
	raise Exception(s)

def d(x):
	p(dir(x))

def t(x):
	p(type(x))

def l(x):
	p(len(x))



def profile(func, *args, **kwargs):
	profile = cProfile.Profile()
	profile.runcall(func, *args, **kwargs)

	res = StringIO.StringIO()
	ps = pstats.Stats(profile, stream=res).sort_stats('cumulative')
	ps.print_stats()

	return res.getvalue()

def p_ex():
	p(exception_trace())

def exception_trace():
	_type, val, tb = sys.exc_info()
	s = '\n'.join(traceback.format_exception( _type, val, tb ))
	return s

def ex():
	return exception_trace()


def counts_per_second(func):
	count = 0
	t = datetime.now() + timedelta(seconds=5)
	while datetime.now() < t:
		func()
		count += 1
	return count / 5.0

import re, md5
import threading
import ProAdmin
import SOAPpy

try:
	from soap.soaputils import VDOM_session_protector
except ImportError:
	from scripting.soap.soaputils import VDOM_session_protector

from utils.exception import VDOMServiceCallError


__version__ = '0.1.6'


session_id_re = re.compile("\<SessionId\>\<\!\[CDATA\[(\S+)\]\]\>\<\/SessionId\>")
session_key_re = re.compile("\<SessionKey\>\<\!\[CDATA\[(\S+)\]\]\>\<\/SessionKey\>")
hash_string_re = re.compile("\<HashString\>\<\!\[CDATA\[(\S+)\]\]\>\<\/HashString\>")
key_re = re.compile("\<Key\>(\S+)_\d+\<\/Key\>")



class VDOMServiceSingleThread(object):
	def __init__(self, url, login, md5hexpass, application_id):
		self._url = url
		self._login = login
		self._md5hexpass = md5hexpass
		self._application_id = application_id

		self._request_num = 0
		self._skey = None
		self._sid = None
		self._skey = None

		self._server = self.__create_soap_proxy(url)
		self._protector = None


	def __create_soap_proxy(self, url):
		if not '://' in url:
			url = 'http://' + url

		if url.lower().startswith('https://'):
			import ssl
			ssl._create_default_https_context = ssl._create_unverified_context

		self._url = url
		return SOAPpy.SOAPProxy(url.rstrip('/') + '/SOAP', namespace='http://services.vdom.net/VDOMServices')



	def __request_skey(self):
		return '{0}_{1:d}'.format(self._skey, self._request_num)


	def open_session(self):
		login_result = self._server.open_session(self._login, self._md5hexpass)

		self._request_num = 0

		self._sid = str(session_id_re.search(login_result, 1).group(1))
		skey = str(session_key_re.search(login_result, 1).group(1))
		hash_string = str(hash_string_re.search(login_result, 1).group(1))

		self._protector = VDOM_session_protector(hash_string)
		self._skey = self._protector.next_session_key(skey)

		return self


	def call(self, container_id, action_name, xml_data):
		xml_param = "<Arguments><CallType>server_action</CallType></Arguments>"
		ret = None

		try:
			ret = self._server.remote_call(self._sid, self.__request_skey(), self._application_id, container_id, action_name, xml_param, xml_data)

		except Exception, ex:
			if ret:
				raise VDOMServiceCallError( str(ret) )
			else:
				raise VDOMServiceCallError( ex.message  )

		if ret == 'None':
			raise VDOMServiceCallError('Session is closed')

		self._skey = self._protector.next_session_key(self._skey)
		self._request_num += 1

		return key_re.sub('', ret)


	def remote(self, method_name, params=None, no_app_id=False):
		params = params or []

		if not no_app_id:
			params.insert(0, self._application_id)

		ret = None
		try:
			soap_method = getattr(self._server, method_name)
			ret = soap_method(self._sid, self.__request_skey(), *params)

		except Exception, ex:
			if ret:
				raise VDOMServiceCallError( str(ret) )
			else:
				raise VDOMServiceCallError( ex.message  )

		self._skey = self._protector.next_session_key(self._skey)
		self._request_num+=1

		return key_re.sub('', ret)


	@classmethod
	def connect(cls, url, login, md5_hexpass, application_id):
		service = cls(url, login, md5_hexpass, application_id)
		return service.open_session()







class VDOMServiceMultiThread(VDOMServiceSingleThread):
	def __init__(self, url, login, md5hexpass, application_id):
		url = 'http://127.0.0.1' if ProAdmin.is_local_host(url) else "https://" + url

		VDOMServiceSingleThread.__init__(self, url, login, md5hexpass, application_id)
		self.__thread = threading.local()


	def api(self):
		if getattr(self.__thread, 'api', None) is None:
			self.__thread.api = VDOMServiceSingleThread(self._url, self._login, self._md5hexpass, self._application_id)
			self.__thread.api.open_session()
		return self.__thread.api


	def open_session( self ):

		self.api().open_session()
		return self


	def call( self, container_id, action_name, xml_data ):
		return self.api().call(container_id, action_name, xml_data)


	def remote(self, method_name, params=None, no_app_id=False):
		return self.api().remote(method_name, params, no_app_id)



VDOMService = VDOMServiceMultiThread
VDOM_service = VDOMServiceMultiThread

