DEFAULT_CUSTOM_EVENT_URL = "/event_icon.svg"
DEFAULT_CUSTOM_EVENT_IMG = "<img src='{}'/>".format(DEFAULT_CUSTOM_EVENT_URL)


class WidgetCustomEvent:
    def __init__(self, show_buttons=True):
        self.__datatable = None
        self._show_buttons = show_buttons
        self.__custom_event_list = None

    def set_data(self, data):
        custom_event = []
        data.sort(key=lambda e: e.name)
        for ce in data:
            id = ce.id
            picture = DEFAULT_CUSTOM_EVENT_IMG
            name = "<span class='name'>" + ce.name + "</span>"
            # edit = "<a class='edit_source' href=''><span>Edit custom event</span></a>"
            delete = "<a class='delete' href=''><span>Delete</span></a>"
            if not self._show_buttons:
                custom_event.append({
                    "id": id,
                    "Picture": picture,
                    "Name": name
                })
                continue
            custom_event.append({
                "id": id,
                "Picture": picture,
                "Name": name,
                # "Edit" : edit,
                "Delete": delete
            })
        self.__custom_event_list = json.dumps(custom_event)

    def render(self, datatable):
        self.__datatable = datatable
        if not self._show_buttons:
            self.__datatable.header = json.dumps(["id", "Picture", "Name"])
        self.__datatable.data = self.__custom_event_list


class WidgetCustomEventTree(PluginDetailsTreeViewBaseWidget):
