        return tuple(
            self.get_obj_action(obj, Commands.custom_event_open_delete_dialog_command),
        )

    @classmethod
    def _get_obj_onclick_command(cls, obj):
        from widget_plugin_details_commands import Commands
        return Commands.custom_event_open_edit_dialog_command

    @classmethod
    def _get_full_name(cls, e):
        return e.name

    @classmethod
    def _get_obj_icon(cls, e):
        return DEFAULT_CUSTOM_EVENT_IMG

    @classmethod
    def _get_obj_ident(cls, e):
        return e.id

from prosuite_localization import get_localization


def handle_errors(growl=None):
    lang = get_localization()
    
    if "error" in session:
        for err in session["error"]:
            if len(err) == 1:
                msg = [lang["error.title"], err[0]]
            else:
                msg = [err[i] for i in range(len(err))]
        
        (growl or self.growl).action("show", msg)
        del session["error"]

from functools import partial
