DEFAULT_MACRO_IMG  = TEMPLATE_MACRO_IMG.format(DEFAULT_MACRO_IMG_URL)
DEFAULT_PLUGIN_IMG = "<img src='/a4013f54-351f-4e51-9afe-c595e5d210b2.png'/>"
ACTIVE_ROW_SPAN =  "<span style='opacity:0.4;top: 0;left: 0; background:#d8dadd; height:100%; width:100%; position:absolute; pointer-events: none;'></span>"


class WidgetMacros:
	def __init__(self, show_buttons = True):
		self.__datatable = None
		self.__macros_list = None
		self._show_buttons = show_buttons

	@classmethod
	def get_macros_icon_url(cls, macros):
		resource_is_exists = lambda res_name: True #ResourceFolderManager(macros.plugin_guid).exists(res_name)
		icon_map = {
			"button": DEFAULT_MACRO_BUTTON_IMG_URL,
			"event": DEFAULT_MACRO_EVENT_IMG_URL,
			"library": DEFAULT_MACRO_LIB_IMG_URL,
			"config": DEFAULT_CONFIG_MACRO_IMG_URL,
			"*": DEFAULT_MACRO_IMG_URL
		}
		macros_type = "config" if cls._macros_is_config(macros) else macros.type
		return u"/get_plugin_resource?guid={}&name={}&type=res".format(
			macros.plugin_guid, macros.macros_picture
		) if macros.macros_picture and resource_is_exists(macros.macros_picture) else icon_map.get(macros_type, icon_map["*"])

	@classmethod
	def get_macros_icon(cls, macros):
		return u"<img src='{}' alt='macros-icon' />".format(cls.get_macros_icon_url(macros))

	def _get_macros_info(self, macros):
		macros_is_active = getattr(macros, 'selected', False)
		active_macros = ACTIVE_ROW_SPAN if macros_is_active else ""
		return active_macros + u"<span class='name'>{}</span>".format(macros.name)

	def _get_macros_description(self, macros):
		tag = "b" if macros.name == "config" else "p"
		result = u"<{tag} class='description'>{d}</{tag}>".format(tag=tag, d=macros.description)
		result = result if macros.description else "<div style='padding-bottom:5px'></div>"
		return result if self._show_buttons else ""

	def _get_edit_info_btn(self):
		return """
			<div class='edit_source' class='disabled_button'>
				<span href=''>Edit info</span>
			</div>
		""" if self._show_buttons else ""

	def _get_delete_btn(self):
		return "<a href='' class='delete'><span>delete</span></a>" if self._show_buttons else ""

	@classmethod
	def get_macros_export_link(cls, macros):
		return _PUBLIC_URL.format(macros.guid, macros.name, "macros")

	def _macros_to_dict(self, macros):
		result = {
			"id": macros.id,
			"Picture": self.get_macros_icon(macros),
			"Macros_info": self._get_macros_info(macros),
		}
		if self._show_buttons:
			result.update({
				"Edit_info": self._get_edit_info_btn(),
				"Delete": self._get_delete_btn(),
				"Description": self._get_macros_description(macros)
			})
		return result

	@classmethod
	def _macros_is_config(cls, macros):
		return macros.name.lower() == "config"

	def set_data(self, data):
		data = data or []
		# config macro always must be first
		data = sorted(data, key=lambda m: m.name.lower() if not self._macros_is_config(m) else 0)
		self.__macros_list = map(self._macros_to_dict, data)

	def _get_header(self):
		header_with_buttons = ["id", "Picture", "Macros_info", "Delete", "Edit_info", "Description"]
		header_without_buttons = header_with_buttons[0:3]
		return header_with_buttons if self._show_buttons else header_without_buttons

	def render(self, datatable):
		self.__datatable = datatable
		self.__datatable.header = json.dumps(self._get_header())
		self.__datatable.data = json.dumps(self.__macros_list)


class DefaultOrderedDict(OrderedDict):
	# Source: http://stackoverflow.com/a/6190500/562769
	def __init__(self, default_factory=None, *a, **kw):
		if (default_factory is not None and
		   not isinstance(default_factory, Callable)):
			raise TypeError('first argument must be callable')
		OrderedDict.__init__(self, *a, **kw)
		self.default_factory = default_factory

	def __getitem__(self, key):
		try:
			return OrderedDict.__getitem__(self, key)
		except KeyError:
			return self.__missing__(key)

	def __missing__(self, key):
		if self.default_factory is None:
			raise KeyError(key)
		self[key] = value = self.default_factory()
		return value

	def __reduce__(self):
		if self.default_factory is None:
			args = tuple()
		else:
			args = self.default_factory,
		return type(self), args, None, None, self.items()

	def copy(self):
		return self.__copy__()

	def __copy__(self):
		return type(self)(self.default_factory, self)

	def __deepcopy__(self, memo):
		import copy
		return type(self)(self.default_factory,
						  copy.deepcopy(self.items()))

	def __repr__(self):
		return '%s (%s, %s)' % (
			self.__class__.__name__,
			self.default_factory,
			OrderedDict.__repr__(self)
		)


class PluginDetailsTreeViewBaseWidget(object):
	def __init__(self, tree):
		self._tree = tree
		self._string = StringIO()
		self._data = []
		self.__prepeared_data = None

	class __metaclass__(type):
		@property
		def Commands(cls):
			from widget_plugin_details_commands import Commands
			return Commands

	@classmethod
	def _get_obj_ident(cls, obj):
		return None

	@classmethod
	def _get_obj_onclick_data(cls, obj):
		return {}

	@classmethod
	def _generate_onclick(cls, obj, command):
		if not command:
			return ""

		return cls.__generate_onclick(
			cls._get_obj_ident(obj),
			command.value,
			json.dumps(cls._get_obj_onclick_data(obj))
		)

	@classmethod
	def __generate_onclick(self, ident, command, data):
		return u"""
			event.stopPropagation()
			execEventBinded($(`body`).attr(`id`).replace(`-`, `_`).slice(2), `custom`, {
			  guid: `%(ident)s`, command: `%(command)s`, data: %(data)s
			});
		""" % {"ident": ident, "command": command, "data": json.dumps(data).replace('"', "&quot;")}

	def set_data(self, data):
		assert isinstance(data, (list, tuple))
		self._data = data

	@classmethod
	def _get_full_name(cls, obj):
		raise NotImplementedError

	@classmethod
	def __get_full_name(cls, obj):
		return cls._get_full_name(obj).replace("\\", "/")

	@classmethod
	def _get_obj_icon(cls, obj):
		raise NotImplementedError

	@classmethod
	def _get_obj_onclick_command(cls, obj):
		return None

	@classmethod
	def _get_obj_icon_onclick_command(cls, obj):
		return None

	@classmethod
	def _get_obj_description(cls, obj):
		return ''

	@classmethod
	def _get_obj_name(cls, obj):
		return cls.__get_full_name(obj).split("/")[-1]

	@classmethod
	def get_obj_action(cls, obj, command):
		return u"""<div onclick="{onclick}" class="action {icon}"></div>""".format(
			icon=command.value.icon.value,
			onclick=cls._generate_onclick(obj, command)
		)

	@classmethod
	def _get_obj_actions(cls, obj):
		return tuple()

	def _open_li_branch(self, id, name):
		self._string.write(
			u"""
				<li id=%(guid)s>
					<span class="">
						<div class='dyn-title'>%(name)s</div>
					</span>
			""" % {'guid': id, 'name': name})

	def _close_li_branch(self):
		self._string.write('</li>')

	def _create_obj_branch(self, obj):
		self._string.write(
		u"""<li id=%(id)s>
			<div data-ident="%(id)s" onclick="%(onclick)s" class='dyn-title'>
				<div onclick="%(icon_onclick)s" class='icon'>%(icon)s</div>
				<div class='name_holder'>
					<div class='name'>%(name)s</div>
					<div class='description'>%(description)s</div>
				</div>
				<div class='actions'>%(actions)s</div>
			</div>
		"""
		% {
			'onclick': self._generate_onclick(obj, self._get_obj_onclick_command(obj)),
			'id': "{}-{}".format(self.__class__.__name__, self._get_obj_ident(obj)),
			'icon': self._get_obj_icon(obj),
			'name': self._get_obj_name(obj),
			'actions': u"".join(self._get_obj_actions(obj)),
			'description': self._get_obj_description(obj),
			'icon_onclick': self._generate_onclick(obj, self._get_obj_icon_onclick_command(obj))
		})

	def _build_tree(self):
		self._string.write(u"<ul>")

		### PREPEARE DATA
		FILE_MARKER = '<files>'

		node_maker = lambda: DefaultOrderedDict(
			dict, ((FILE_MARKER, []),)
		)

		self.__prepeared_data = node_maker()

		split_name = lambda x: self.__get_full_name(x).split("/")
		is_into_folder = lambda x: len(split_name(x)) > 1
		group_lambda = lambda x: tuple(split_name(x)[:-1])

		folder_items = tuple(
			sorted(
				(obj for obj in self._data if is_into_folder(obj)),
				key=split_name
			)
		)

		root_items = sorted((obj for obj in self._data if not is_into_folder(obj)), key=self.__get_full_name)

		for group, group_items in groupby(folder_items, key=group_lambda):
			group_len = len(group)
			trunk = self.__prepeared_data
			for i, g in enumerate(group):
				if g in trunk:
					trunk = trunk[g]
				else:
					node = node_maker()
					trunk[g] = node
					trunk = node
				if i == group_len - 1:
					trunk[FILE_MARKER].extend(sorted(group_items))

		### BUILD TREE
		self.__prepeared_data[FILE_MARKER].extend(root_items)
		def render(d):
			# files to end
			d[FILE_MARKER] = d.pop(FILE_MARKER)
			for key, value in d.items():
				if key == FILE_MARKER:
					if value:
						for obj in value:
							self._create_obj_branch(obj)
				else:
					self._open_li_branch("group-" + str(key), key)
					self._string.write(u"<ul>")
					if isinstance(value, dict):
						render(value)
			self._string.write(u"</ul>")
			self._string.write(u"</li>")
		render(self.__prepeared_data)
		###

		#end all
		self._string.write(u"</ul>")
		result = self._string.getvalue()
		self._string.close()
		return result

	def render(self):
		self._tree.data = self._build_tree()


