PICTURE = "<img src='db_icon.svg' />"


class WidgetPluginDB:
    def __init__(self, show_buttons=True):
        self.__db_list = None
        self._show_buttons = show_buttons

    def _db_to_dict(self, db):
        result = {
            "id": db,
            "Picture": PICTURE,
            "Name": "<span class='name'>" + db + "</span>"
        }
        if self._show_buttons:
            result.update({
                "Import": "<a class='update' href=''><span>Update</span></a>",
                "Export": "<a class='download' href=''><span>Download</span></a>",
                "Delete": "<a class='delete' href=''><span>Delete</span></a>"
            })
        return result

    def set_data(self, data):
        self.__db_list = json.dumps(map(self._db_to_dict, sorted(data or [])))

    def render(self, datatable=None):
        header = ("id", "Picture", "Name")
        if self._show_buttons:
            header += ("Delete", "Export", "Import")
        datatable.header = json.dumps(header)
        datatable.data = self.__db_list


