    def __init__(self, tree, plugin=None):
        super(WidgetPluginDBTree, self).__init__(tree)
        self._plugin = plugin
        self.set_data(
            getattr(DatabaseManager(self._plugin.guid), "databaselist", tuple())
        )

    def _get_obj_actions(self, obj):
        from widget_plugin_details_commands import Commands
        return (
            self.get_obj_action(obj, Commands.database_open_delete_dialog_command),
            self.get_obj_action(obj, Commands.database_export_command),
            self.get_obj_action(obj, Commands.database_open_update_dialog_command)
        )

    @classmethod
    def _get_full_name(cls, db):
        return db

    @classmethod
    def _get_obj_icon(cls, db):
        return PICTURE

    @classmethod
    def _get_obj_id(cls, db):
        return db


def handle_upload_db_request(page, request, update=False):
    plugin = Plugins.get_by_id(int(request.arguments.get("id", -1)))
    lang = get_localization()
    db_file = request.arguments.get("uploader_db", "", castto=Attachment)
    db_file_handler = getattr(db_file,'handler', io.BytesIO())
    db_name = request.arguments.get("formtext_db_name")
    db_name = db_name or getattr(db_file, 'name', '')
    db_manager = DatabaseManager(plugin.guid)

    if update:
        db_name = request.arguments.get("formtext_old_db_name", "")

    if db_name in db_manager.databaselist and not update:
        page.growl.title = lang["error.title"]
        page.growl.text = lang["plugins.db.name.error"].format(db_name)
        page.growl.active = "1"
        return

    db_manager.import_db(db_name, db_file_handler)

def handle_export_db_request(page, request):
    plugin = Plugins.get_by_id(int(request.arguments.get("id", -1)))
    db_name = request.arguments.get("db", "")
    output = DatabaseManager(plugin.guid).export_db(db_name)
    outp = io.BytesIO()
    outp.write(output.read())
    outp_len = outp.tell()
    outp.seek(0)
    response.send_file(
    	db_name if db_name.endswith(".sqlite") else db_name + ".sqlite",
    	outp_len,
    	outp,
    	cache_control=False
    )

from collections import OrderedDict
import inspect
import json

from VEE_prosuite_events import VEE_PluginIDEExtension
from prosuite_localization import get_localization
from VEE_resources import ResourceFolderManager
from VEE_sqlite3 import DatabaseManager

from class_plugins import Plugins
from class_macro import Macros

from widget_macro import (
    WidgetMacrosExtensionTree,
    DEFAULT_MACRO_IMG_URL,
    WidgetMacrosTree,
    WidgetMacros
)
from widget_resource import (
    Button as EditorTabButton,
    WidgetResourceTree,
    WidgetResource
)
from widget_custom_event import (
    WidgetCustomEventTree,
    WidgetCustomEvent
