from widget_plugins import WidgetPlugins


class PluginDetailsWidget(object):
    def __init__(self, page, plugin=None):
        self.page = page
        plugin = plugin or self.__class__.plugin_id
        self.plugin = plugin if isinstance(plugin, Plugins) else Plugins.get_by_id(plugin)
        self.__IS_ONLOAD = request.render_type != 'e2vdom'

    def _redirect_to_plugins(self):
        response.redirect("/plugins") or self.page.action('goTo', "/plugins")

    def close_opened_tab(self, tab_info):
        opened_tabs = PluginDetailsWidget.opened_tabs
        try:
            tab_info_index = opened_tabs.index(tab_info)
        except ValueError:
            return
        opened_tabs.pop(tab_info_index)
        PluginDetailsWidget.opened_tabs = opened_tabs
        if opened_tabs:
            if tab_info == self.__class__.active_tab:
                new_tab_index = tab_info_index - 1
                new_tab_index = 0 if new_tab_index < 0 else new_tab_index
                new_tab = opened_tabs[new_tab_index]
                PluginDetailsWidget.active_tab = new_tab
                self.open_tab(new_tab)
        else:
            PluginDetailsWidget.active_tab = None
            self.render_macros_datatable()
            self.render_res_buttons()
            self.render_res_datatable()
            self.page.wrapper.rightcol.action("hide")
        self.update_url()

    def close_current_tab(self):
        self.close_opened_tab(PluginDetailsWidget.active_tab)

    def open_resource_preview(self, res_name):
        self.page.wrapper.third_column.action('toggleClass', 'hide')
        self.page.wrapper.third_column.dynamicvdom.action('hide')
        self.page.wrapper.third_column.iframe.action('show')
        self.page.wrapper.third_column.iframe.htmlcode = """
            <iframe 
                style="width: 100%; height: 100%; border: none;" 
                src='/get_plugin_resource_preview?guid={}&name={}'>
            </iframe>
        """.format(self.plugin.guid, res_name)

    class __metaclass__(type):
        @property
        def opened_tabs(cls):
            return request.shared_variables[inspect.currentframe().f_code.co_name] or []

        @opened_tabs.setter
        def opened_tabs(cls, value):
            response.shared_variables[inspect.currentframe().f_code.co_name] = value

        @property
        def active_tab(cls):
            return request.shared_variables[inspect.currentframe().f_code.co_name] or ["", ""]

        @property
        def plugin_id(cls):
            return request.shared_variables[inspect.currentframe().f_code.co_name] or None

        @plugin_id.setter
        def plugin_id(cls, value):
            response.shared_variables[inspect.currentframe().f_code.co_name] = value

        @active_tab.setter
        def active_tab(cls, value):
            response.shared_variables[inspect.currentframe().f_code.co_name] = value

    def update_url(self):
    	self.render_tabs()
        self.page.action(
            'custom',
            '''setTimeout(PluginDetailsRouter.change_route, 100)'''
        )

    def open_tab(self, tab_info):
        self.show_editor()
        opened_tabs = PluginDetailsWidget.opened_tabs
        if tab_info not in opened_tabs:
            opened_tabs.append(tab_info)
            PluginDetailsWidget.opened_tabs = opened_tabs
        monaco_tab = self._build_editor_tab_json(tab_info)
        self.page.action(
            'custom',
            '''
            monacoEditor.addModel("{id}", JSON.parse(`{value}`)).then(() => {{
                monacoEditor.model = "{id}";
            }});
            $(`[data-ident]`).parent().parent().removeClass("dynatree-active");
            $(`[data-ident="{active_selector}"]`).parent().parent().addClass("dynatree-active");
            '''.format(
            	value=json.dumps(monaco_tab), 
            	id="-".join(tab_info),
            	active_selector="{}-{}".format(
            		{"res": "WidgetResourceTree", "macros": "WidgetMacrosTree"}[tab_info[0]],
            		tab_info[1]
            	)
            )
        )
        PluginDetailsWidget.active_tab = tab_info
        self.render_res_buttons()
        self.update_url()
        self.page.wrapper.rightcol.action('show' if opened_tabs else "hide")

    def render_tabs(self, opened_tabs=None):
        editor_tabs = self.page.wrapper.rightcol.form_macros.toppanel.tabs
        opened_tabs = opened_tabs or PluginDetailsWidget.opened_tabs
        editor_tabs.data = json.dumps(
            OrderedDict([self._build_tab_button(tab) for tab in opened_tabs])
        )
        editor_tabs.activeitem = u"-".join(PluginDetailsWidget.active_tab)

    def render_editor(self):
        editor = self.page.wrapper.rightcol.form_macros.editor.codeeditor_macros_body
        opened_tabs = PluginDetailsWidget.opened_tabs
        if self.__IS_ONLOAD:
            self.page.wrapper.rightcol.visible = "1" if opened_tabs else "0"
        else:
            self.page.wrapper.rightcol.action('show' if opened_tabs else "hide")
        if not(opened_tabs):
            return
        self.render_tabs(opened_tabs)
        editor.value = json.dumps(
            {"-".join(tab): self._build_editor_tab_json(tab) for tab in opened_tabs}
        )

    def _build_tab_button(self, tab_info):
        tab_type, tab_id = tab_info
        if tab_type == "macros":
            m = Macros.get_by_id(tab_id)
            icon = WidgetMacros.get_macros_icon_url(m)
            label = m.name
        if tab_type == "res":
            icon = WidgetResource.get_extension_instance_by_res(tab_id).icon or DEFAULT_MACRO_IMG_URL
            label = tab_id
        return (u"{}-{}".format(tab_type, tab_id), unicode(EditorTabButton(label=label, icon=icon)))

    def _build_editor_tab_json(self, tab_info):
        tab_type, tab_id = tab_info
        url = ""
        language = ""
        if tab_type == "macros":
            m = Macros.get_by_id(tab_id)
            url = WidgetMacros.get_macros_export_link(m)
            language = "vb"
        if tab_type == "res":
            w = WidgetResource()
            w._plugin_guid = self.plugin.guid
            url = w.get_res_export_link(tab_id)
            language = WidgetResource.get_extension_instance_by_res(tab_id).monaco_lang
        return {"url": url, "language": language, "active": tab_id == PluginDetailsWidget.active_tab[1]}

    def render_macros_datatable(self, macroses=None):
        tree = self.page.wrapper.leftcol.datatable_macros_holder.tree
        WidgetMacrosTree(tree, self.plugin).render()
        self.__after_render_tree(tree)

        self.render_extensions_datatable()

    def __after_render_tree(self, tree):
        if self.__IS_ONLOAD:
            tree.visible = "0"
            self.page.customheaders += """
            <script>
            	document.addEventListener('DOMContentLoaded', () => {
            		$(`#o_%s`).css('display', 'block');
				});
            </script>
            """ % str(tree.id).replace("-", "_")


    def render_timer_datatable(self):
        tree = self.page.wrapper.leftcol.datatable_timer_holder.tree
        WidgetTimerTree(tree, self.plugin).render()
        self.__after_render_tree(tree)

    def render_plugin_db_datatable(self):
        tree = self.page.wrapper.leftcol.datatable_db_holder.tree
        WidgetPluginDBTree(tree, self.plugin).render()
        self.__after_render_tree(tree)

    def render_custom_event_datatable(self):
        tree = self.page.wrapper.leftcol.custom_event_holder.tree
        WidgetCustomEventTree(tree, self.plugin).render()
        self.__after_render_tree(tree)

    def render_extensions_datatable(self, macroses=None):
        macroses = macroses or VEE_PluginIDEExtension(self.page, self.plugin.guid).macroses
        if not macroses:
            self.page.wrapper.leftcol.datatable_extension_holder.visible = "0"
            return
        else:
            self.page.wrapper.leftcol.datatable_extension_holder.visible = "1"
        tree = self.page.wrapper.leftcol.datatable_extension_holder.tree
        WidgetMacrosExtensionTree(tree, macroses).render()
        self.__after_render_tree(tree)

    def render_res_datatable(self):
        tree = self.page.wrapper.leftcol.datatable_resource_holder.tree
        WidgetResourceTree(tree, self.plugin).render()
        self.__after_render_tree(tree)

    def render_res_buttons(self):
        tab_type, tab_id = PluginDetailsWidget.active_tab
        self.page.wrapper.rightcol.form_macros.toppanel.buttons.data = json.dumps(
            WidgetResource.get_res_buttons(tab_id) if tab_type == "res" else {}
        )

    def render_plugin_info(self):
        widget_plugins = WidgetPlugins()
        widget_plugins.set_single_data(self.plugin)
        widget_plugins.render(richtext=self.page.toppanel.richtext_plugin)

    def render_config_btn(self):
        config_is_exist = bool(filter(lambda m: m.name == 'config', self.plugin.get_macros()))
        self.page.toppanel.action_menu.button_config.visible = "1" if config_is_exist else "0"
        self.page.toppanel.action_menu.button_create_config.visible = "0" if config_is_exist else "1"

    def show_editor(self):
        self.page.wrapper.rightcol.action("removeClass", "hide")
        self.page.wrapper.third_column.action("addClass", "hide")

    def show_extension_screen(self):
        self.page.wrapper.rightcol.action("addClass", "hide")
        self.page.wrapper.third_column.dynamicvdom.action('show')
        self.page.wrapper.third_column.iframe.action('hide')
        self.page.wrapper.third_column.action("removeClass", "hide")

    def hide_extension_screen(self):
        self.page.wrapper.third_column.classname += " hide"

    def localize(self):
        lang = get_localization()
        self.page.wrapper.leftcol.datatable_macros_holder.table_panel.text_macros.value = lang["plugins_details.left_col.macros.title"]
        self.page.wrapper.leftcol.datatable_resource_holder.table_panel.text_resources.value = lang["plugins_details.left_col.resources.title"]
        self.page.wrapper.leftcol.datatable_timer_holder.table_panel.text_timer.value = lang["plugins_details.left_col.timers.title"]
        self.page.wrapper.leftcol.datatable_db_holder.table_panel.text_db.value = lang["plugins_details.left_col.db.title"]
        self.page.wrapper.leftcol.custom_event_holder.table_panel.text_custom_events.value = lang["plugins_details.left_col.custom.title"]

    def validate_plugin(self):
        return bool(self.plugin)

    def render(self):
        self.validate_plugin() or self._redirect_to_plugins()

        self.__class__.plugin_id = self.plugin.id

        self.render_config_btn()
        self.render_plugin_info()
        self.render_macros_datatable()
        self.render_timer_datatable()
        self.render_plugin_db_datatable()
        self.render_custom_event_datatable()
        self.render_res_datatable()
        self.render_editor()
        self.render_res_buttons()

        self.hide_extension_screen()

        self.localize()

        self.update_url()

