class ActionIcon(enum.Enum):
    edit = 'edit'
    edit_source = 'edit_source'
    delete = 'delete'
    update = 'update'
    download = 'download'


class CommandBase(type):
    icon = None

    def __str__(cls):
        return cls.__name__

    def __repr__(cls):
        return str(cls)

    @classmethod
    def run(cls, page, request):
        raise NotImplementedError

    @classmethod
    def _run(cls, page, object):
        raise NotImplementedError


# base commands
class MacrosCommandBase():
    __metaclass__ = CommandBase

    @classmethod
    def run(cls, page, request):
        cls._run(page, request)


class TimerCommandBase():
    __metaclass__ = CommandBase

    @classmethod
    def run(cls, page, request):
        cls._run(page, request)


class CustomEventCommandBase():
    __metaclass__ = CommandBase

    @classmethod
    def run(cls, page, request):
        cls._run(page, request)


class DatabaseCommandBase():
    __metaclass__ = CommandBase

    @classmethod
    def run(cls, page, request):
        cls._run(page, request)


class ResourceCommandBase():
    __metaclass__ = CommandBase

    @classmethod
    def run(cls, page, request):
        cls._run(page, request)


class ExtensionCommandBase():
    __metaclass__ = CommandBase

    @classmethod
    def run(cls, page, request):
        cls._run(page, request)


# macros commands
