# resource command
class ResourceExportCommand(ResourceCommandBase):
    icon = ActionIcon.download

    @classmethod
    def _run(cls, page, request):
        w = WidgetResource()
        w._plugin_guid = Plugins.get_by_id(request.shared_variables["plugin_id"]).guid
        page.action("custom", """
        {
            const downloadLink = $(`<a target="_blank" style="display: none !important" href="%(link)s" download></a>`);
            $('body').append(downloadLink);
            downloadLink.get(0).click();
            downloadLink.remove();
        }
        """ % {'link': w.get_res_export_link(request.arguments["guid"])})


