# extension commands
class RunExtensionCommand(ExtensionCommandBase):
    @classmethod
    def _run(cls, page, request):
        VEE_PluginIDEExtension(
            page,
            macros_id=request.arguments["guid"],
            dynamicvdom=page.wrapper.third_column.dynamicvdom,
            css_container=page.wrapper.third_column.ext_scss,
            js_container=page.wrapper.third_column.ext_js,
            source_plugin=Plugins.get_by_id(request.shared_variables['plugin_id'])
        ).activate()
        PluginDetailsWidget(page).show_extension_screen()


class Commands(enum.Enum):
    # macros
    open_macros_edit_dialog = MacrosEditCommand
    open_macros_tab = MacrosOpenTabCommand
    open_delete_macros_dialog = MacrosOpenDeleteDialogCommand
    macros_edit_icon_command = MacrosEditIconCommand

    # timer
    timer_open_delete_dialog_command = TimerOpenDeleteDialogCommand
    timer_open_edit_dialog_command = TimerOpenEditDialogCommand

    # custom event
    custom_event_open_delete_dialog_command = CustomEventOpenDeleteDialogCommand
    custom_event_open_edit_dialog_command = CustomEventOpenEditDialogCommand

    # database
    database_open_delete_dialog_command = DatabaseOpenDeleteDialogCommand
    database_open_update_dialog_command = DatabaseOpenUpdateDialogCommand
    database_export_command = DatabaseExportCommand

    # resource
    resource_open_tab_command = ResourceOpenTabCommand
    resource_export_command = ResourceExportCommand
    resource_open_delete_dialog_command = ResourceOpenDeleteDialogCommand
    resource_open_edit_dialog_command = ResourceOpenEditDialogCommand

    # extension
    run_extension_command = RunExtensionCommand


class CommandEngine():
    @classmethod
    def handle_command(cls, page, request):
        command_name = request.arguments.get("command")
        command = filter(lambda x: str(x.value) == command_name, Commands)
        command = command[0] if command else None
        if command is None:
            raise ValueError('Command "{}" not found'.format(command_name))

        command.value.run(page, request)

import json
import json
from class_macro import Macros
from prosuite_localization import get_localization
from VEE_resources import ResourceFolderManager


class WidgetPlugins:
    def __init__(self):
        self.__datatable = None
        self.__richtext = None
        self.__plugins_list = None
        self.__richtext_value = None
        self.lang = get_localization()

    def __handle_none_value(self, value, _bool):
        if _bool:
            return value
        else:
            return ""

    @classmethod
    def get_plugin_image_url(cls, plugin):
        # this is lambda for optimization
        resource_is_exists = lambda r_name: True #r_name in ResourceFolderManager(plugin.guid).resourcelist    	
        return "/get_plugin_resource?guid={}&name={}&type=res".format(
            plugin.guid, plugin.picture
        ) if plugin.picture and resource_is_exists(plugin.picture) else "/814fe134-0a79-4371-bfac-74539d57d98c.res"

    def get_plugin_image(self, plugin):
        return "<img src='{}' />".format(self.get_plugin_image_url(plugin))

    def set_data(self, data):
        plugins = []
        data.sort(key=lambda p: p.name.lower())
        for plugin in data:
            config_macros = Macros.get_config_macro(plugin.guid)
            # *******PLUGIN INFO**********
            plugin_verison = self.__handle_none_value("<p class='version'>%s %s</p>"%(self.lang["plugins.plugin.version"],plugin.version), plugin.version)
            #("<h2 class='author'>by " + plugin.author + "</h2>") if plugin.author else "<h2 class='author'></h2>"
            plugin_author = self.__handle_none_value("<h2 class='author'>%s %s</h2>"%(self.lang["plugins.plugin.by"], plugin.author), plugin.author)
            plugin_description = self.__handle_none_value("<p class='description'>%s</p>"%(plugin.description), plugin.description)
            plugin_name = "<h1 class='name'>%s</h1>"%(plugin.name)
            plugin_info = "<a href=''>%s %s %s %s</a>"%(plugin_name, plugin_verison, plugin_author, plugin_description)
            # *******END PLUGIN INFO**********
            plugins.append({
                "id": plugin.id,
                "Picture": self.get_plugin_image(plugin),
                "Plugin_info": plugin_info,
                "Update": "<a href=''>%s</a>"%(self.lang["plugins.plugin.update"]),
                "Export": "<a href=''>%s</a>"%(self.lang["plugins.plugin.export"]),
                "Delete": "<a href=''>%s</a>"%(self.lang["plugins.plugin.uninstall"]),
                #"MD5": "<a href=''>%s</a>"%(lang["plugins.plugin.md5"]),
                "Config": "<a href=''>%s</a>"%(self.lang["plugins.plugin.config"]) if config_macros else "<a class='disabled' href=''>%s</a>" % self.lang["plugins.plugin.config"],
                "Options": "<span cell-number='{}'>".format(len(plugins)+1)

            })
        self.__plugins_list = json.dumps(plugins)

    def set_single_data(self, data):
        img = self.get_plugin_image(data)
        self.__richtext_value = img + "<h1 class='name'>%s</h1><h2 class='author'>%s %s</h2>"%(data.name, self.lang["plugins.plugin.by"], data.author)

    def render(self, datatable=None, richtext=None):
        if datatable:
            self.__datatable = datatable
            self.__datatable.data = self.__plugins_list
        if richtext:
            self.__richtext = richtext
            self.__richtext.value = self.__richtext_value

import json
from VEE_resources import ResourceFolderManager
import mimetypes
import os
from uuid import uuid4
