DEFAULT_RES_IMAGE_URL = '/resource_icon.png'
DEFAULT_RES_IMAGE = "<img src='{}' />".format(DEFAULT_RES_IMAGE_URL)


class WidgetResource:
    def __init__(self, selected_name=''):
        self.selected_name = selected_name
        self._resource_list = None
        self._plugin_guid = None

#    def __order_position(self, res):
#        """
#            return (extension, extension_position, resource_name)
#        """
#        ext = self.get_res_ext(res)
#
#        # [].index throw exception if value not in array
#        # this is trick for safely getting order position
#        ext_index = (i for i, v in enumerate(EXTENSIONS_CONFIG) if v.name == ext)
#        ext_index = next(ext_index, len(EXTENSIONS_CONFIG) + 1)
#        # end trick
#
#        return (ext_index, ext, res)

    def __order_position(self, res):
        return res

    @classmethod
    def get_res_ext(cls, res):
        return os.path.splitext(res)[1][1:].lower()

    def _get_img(self, url):
        url = url if url.startswith("/") else '/' + url
        return "<img alt='res-preview' src='{}'>".format(url)

    @classmethod
    def get_extension_instance(cls, ext):
        return next(iter(filter(lambda e: ext in ((e.name, ) + e.aliases), EXTENSIONS_CONFIG)), None)

    @classmethod
    def get_extension_instance_by_res(cls, res):
        return cls.get_extension_instance(cls.get_res_ext(res))

    @classmethod
    def get_res_buttons(cls, res):
        if res is None:
            return {}

        extension = cls.get_extension_instance(cls.get_res_ext(res))
        return OrderedDict(
            ((button.uuid, str(button)) for button in getattr(extension, 'buttons', []))
        )

    def get_res_image(self, res):
        mimetype = mimetypes.guess_type(res, strict=False)[0]
        ext = self.get_res_ext(res)
        print mimetype, res, type(mimetype), mimetype and "image" in mimetype, self.get_extension_instance(ext)
        mimetype_is_image = mimetype and "image" in mimetype

        custom_icon = getattr(self.get_extension_instance(ext), 'icon', None)
        if custom_icon:
            picture = custom_icon(res) if callable(custom_icon) else self._get_img(custom_icon)
        elif ext in EDITABLE_EXTENSIONS:
            picture = DEFAULT_MACRO_IMG
        elif mimetype_is_image:
            picture = "<img class='res_icon non-pointer-events' src='{}' />".format(
                self.get_res_export_link(res)
            )
        else:
            picture = DEFAULT_RES_IMAGE
        return picture

    def _get_res_name(self, res):
        active_res = ACTIVE_ROW_SPAN if res == self.selected_name else ""
        return active_res + '<span class="name {}">{}</span>'.format(
            "disable non-pointer-events" if not (self.get_res_ext(res) in EDITABLE_EXTENSIONS) else "",
            res
        )

    def get_res_export_link(self, res):
        return ResourceFolderManager(self._plugin_guid).public_link(res)

    def _get_res_export_btn(self, res):
        return "<a class='download' download href='{}'></a>".format(self.get_res_export_link(res))

    def _resoure_to_dict(self, res):
        return {
            "id": res,
            "Picture": self.get_res_image(res),
            "Name": self._get_res_name(res),
            "Export": self._get_res_export_btn(res),
            "Edit": "", # TODO it's raname if resource "<span class='edit'></span>",
            "Delete": "<span class='delete'></span>"
        }

    def set_data(self, data, plugin_guid):
        self._plugin_guid = plugin_guid
        self._resource_list = map(self._resoure_to_dict, sorted(data, key=self.__order_position))

    def render(self, datatable=None):
        if datatable:
            datatable.header = json.dumps(("id", "Picture", "Name", "Delete", "Export", "Edit"))
            datatable.data = json.dumps(self._resource_list)


class WidgetResourceTree(PluginDetailsTreeViewBaseWidget):
    def __init__(self, tree, plugin=None):
        super(WidgetResourceTree, self).__init__(tree)
        self._plugin = plugin
        data = getattr(ResourceFolderManager(self._plugin.guid), "resourcelist", tuple())
        self.set_data(data)
        self.__widget = WidgetResource()
        self.__widget._plugin_guid = self._plugin.guid

    @classmethod
    def _get_obj_actions(cls, obj):
        return (
            cls.get_obj_action(obj, cls.Commands.resource_open_delete_dialog_command),
            cls.get_obj_action(obj, cls.Commands.resource_export_command),
            # cls.get_obj_action(obj, cls.Commands.resource_open_edit_dialog_command)
        )

    @classmethod
    def _get_obj_onclick_command(cls, obj):
        if obj.split(".")[-1] in EDITABLE_EXTENSIONS:
            return cls.Commands.resource_open_tab_command

    @classmethod
    def _get_full_name(cls, res):
        return res

    def _get_obj_icon(self, res):
        return self.__widget.get_res_image(res)

    @classmethod
    def _get_obj_ident(cls, res):
        return res.replace("\\", "/")


class Extension(object):
    def __init__(self, name, aliases=None, icon=None, editable=True, buttons=None, monaco_lang=""):
        self.name = name
        self.aliases = aliases or tuple()
        self.icon = icon
        self.editable = editable
        self.buttons = buttons or tuple()
        self.monaco_lang = monaco_lang or self.name


class Button(object):
    def __init__(self, label, icon, handler=None, uuid=uuid4()):
        self.uuid = str(uuid)
        self.label = label
        self.icon = icon
        self.handler = handler or self._default_handler

    def _default_handler(self, page, request):
        page.growl.action('show', ('Warning', 'Override button handler.'))

    def __str__(self):
        return u"""<input
            style='background-image: url({icon}) !important; background-size: 17px'
            type='button'
            value='{label}'
            class='act_formbut proshare-save'>""".format(
            label=self.label,
            icon=self.icon
        )


class OpenResPreviewButton(Button):
    def __init__(self, label='Preview', icon='preview-svgrepo-com.svg', handler=None):
        super(OpenResPreviewButton, self).__init__(
            label=label,
            icon=icon,
            handler=handler
        )

    def _default_handler(self, page, request):
        from widget_plugin_details import PluginDetailsWidget
        tab_type, tab_id = PluginDetailsWidget.active_tab
        PluginDetailsWidget(page, request.shared_variables['plugin_id']).open_resource_preview(tab_id)


def handle_button_click(page, request):
    buttons = { button.uuid: button.handler for extension in EXTENSIONS_CONFIG for button in extension.buttons }
    button_uuid = request.arguments.get('itemid', None)
    handler = buttons.get(
        button_uuid,
        lambda page, request: page.gorwl.action('show', ("Eror", "Handler not found"))
    )(page, request)


# define order of sort by ext
EXTENSIONS_CONFIG = (
    Extension('css', icon='css_ext_icon.svg'),
    Extension('e2vdom', icon=DEFAULT_CUSTOM_EVENT_URL, monaco_lang="vb"),
    Extension('vxml', buttons=(OpenResPreviewButton(), ), monaco_lang="xml"),
    Extension('xml'),
    Extension('js', icon='js_ext_icon.svg', aliases=('javascript', ), monaco_lang="javascript"),
    Extension('txt'),
    Extension('html', icon='html_ext_icon.svg'),
    Extension('scss', icon='sass_ext_icon.svg', monaco_lang="scss"),
    Extension('py', aliases=('python', ), icon='py_ext_icon.svg'),
    Extension('vscript', icon=DEFAULT_MACRO_IMG_URL),
    Extension('json', icon='json_ext_icon.svg'),
    Extension('zip', aliases=('rar', ), icon='archive_ext_icon.svg', editable=False),
    Extension('pdf', icon='pdf_ext_icon.svg', editable=False),
    Extension('docx', icon='docx_ext_icon.svg', editable=False),
)
EDITABLE_EXTENSIONS = [
	alias for extension in EXTENSIONS_CONFIG 
	for alias in extension.aliases + (extension.name, ) if extension.editable
]

