PICTURE = "<img src='/timer_icon.svg' />"


class WidgetTimer:
    def __init__(self, show_buttons=True):
        self._show_buttons = show_buttons
        self.__datatable = None
        self.__timer_list = None

    def set_data(self, data):
        timer = []
        data.sort(key=lambda t: t.name.lower())
        for t in data:
            id = t.id
            name = "<span class='name'>" + t.name + "</span>"
            period = "<span class='interval'>" + get_period(t.period.split(":")) + "</span>"
            # edit = "<a class='edit' href=''><span>Edit timer</span></a>"
            delete = "<a class='delete' href=''><span>Delete</span></a>"
            if not self._show_buttons:
                timer.append({
                    "id": id,
                    "Picture": PICTURE,
                    "Name": name + period
                })
                continue
            timer.append({
                "id": id,
                "Picture": PICTURE,
                "Name": name + period,
                # "Edit" : edit,
                "Delete": delete
            })

        self.__timer_list = json.dumps(timer)

    def render(self, datatable):
        self.__datatable = datatable
        # self.__datatable.header = json.dumps(["id", "Picture", "Name", "Edit", "Delete"])
        self.__datatable.header = json.dumps(["id", "Picture", "Name", "Delete"])
        if not self._show_buttons:
            self.__datatable.header = json.dumps(["id", "Picture", "Name"])
        self.__datatable.data = self.__timer_list


