def get_period(period):
    lang = get_localization()
    temp = "%s %s "
    period_str = lang["plugins.timer.once"]
    if period == ['00', '00', '00', '00']:
	    return lang["plugins.timer.not_active"]
    if len(period) == 4:
        if int(period[0]) > 1:
            period_str += temp%(int(period[0]), lang["plugins.timer.days"])
        elif int(period[0]) == 1:
            period_str += temp%(int(period[0]), lang["plugins.timer.day"])
        if int(period[1]) > 1:
            period_str += temp%(int(period[1]), lang["plugins.timer.hours"])
        elif int(period[1]) == 1:
            period_str += temp%(int(period[1]), lang["plugins.timer.hour"])
        if int(period[2]) > 1:
            period_str += temp%(int(period[2]), lang["plugins.timer.mins"])
        elif int(period[2]) == 1:
            period_str += temp%(int(period[2]), lang["plugins.timer.min"])
        if int(period[3]) > 1:
            period_str += temp%(int(period[3]), lang["plugins.timer.secs"])
        elif int(period[3]) == 1:
            period_str += temp%(int(period[3]), lang["plugins.timer.sec"])
    if len(period) == 3:
        if int(period[1]) > 1:
            period_str += temp%(int(period[1]), lang["plugins.timer.hours"])
        elif int(period[1]) == 1:
            period_str += temp%(int(period[1]), lang["plugins.timer.hour"])
        if int(period[2]) > 1:
            period_str += temp%(int(period[2]), lang["plugins.timer.mins"])
        elif int(period[2]) == 1:
            period_str += temp%(int(period[2]), lang["plugins.timer.min"])
        if int(period[3]) > 1:
            period_str += temp%(int(period[3]), lang["plugins.timer.secs"])
        elif int(period[3]) == 1:
            period_str += temp%(int(period[3]), lang["plugins.timer.sec"])
    if len(period) == 2:
        if int(period[2]) > 1:
            period_str += temp%(int(period[2]), lang["plugins.timer.mins"])
        elif int(period[2]) == 1:
            period_str += temp%(int(period[2]), lang["plugins.timer.min"])
        if int(period[3]) > 1:
            period_str += temp%(int(period[3]), lang["plugins.timer.secs"])
        elif int(period[3]) == 1:
            period_str += temp%(int(period[3]), lang["plugins.timer.sec"])
    if len(period) == 1:
        if int(period[3]) > 1:
            period_str += temp%(int(period[3]), lang["plugins.timer.secs"])
        elif int(period[3]) == 1:
            period_str += temp%(int(period[3]), lang["plugins.timer.sec"])

    return period_str

import ProAdmin
from io import StringIO

NAME_MAX_LENGTH = 30
MAX_LENGTH_STRING_END = "..."

class WidgetUserAndGroupDeleteDialog(object):

	def __init__(self,list, current_tab ):
		self.__list = list 	# list of ID's (GUID)
		self.__subjects = None
		self.__current_tab = current_tab


	def set_subjects( self ):

		self.__subjects = 	ProAdmin.application().get_users() \
							if self.__current_tab == "users" else\
							ProAdmin.application().get_groups()

	def delete_subjects(self):

		self.set_subjects()
		for subject in self.__subjects: # should be initialized during render()
			if subject.guid in self.__list:
				self.__list.remove( subject.guid )
				subject.delete()

	def render(self,dialog, html):
		result_str = StringIO()
		self.set_subjects()
		template = 	u"<div class='acl_container_user'><b>%(name)s</b> </div>" \
					if self.__current_tab == "users" else\
					u"<div class='acl_container_group'><b>%(name)s</b> </div>"


		for subject in self.__subjects:
			if subject.guid in self.__list:
				result_str.write( template % {
						'name'	: subject.name[:NAME_MAX_LENGTH]+MAX_LENGTH_STRING_END if len( subject.name ) > NAME_MAX_LENGTH else subject.name
					})

		html.action( "setHTML", [ result_str.getvalue() ] )
		result_str.close()

		dialog.action( "show", [ "" ] )

import ProAdmin
import json
from prosuite_localization import get_localization


GROUP_MAX_LENGTH = 30
USER_NAME_MAX_LENGTH = 23
USER_LOGIN_MAX_LENGTH = 18
MAX_LENGTH_STRING_END = "..."


class WidgetUserGroupDialogDatatable(object):
    def __init__(self, search_obj_type, obj):
        self.search_obj_type = search_obj_type
        self.obj = obj
        self.selected_rows = []
        self.get_data = None
        self.lang = get_localization()

    def set_data(self):
        if self.search_obj_type == "user":
            self.get_data = ProAdmin.application().get_users
        else:
            self.get_data = ProAdmin.application().get_groups

    def set_selected_rows(self):
        if self.obj:
            if self.search_obj_type == "user":
                for obj in self.obj.get_users():
                    self.selected_rows.append(obj.guid)
            else:
                for obj in self.obj.get_groups():
                    self.selected_rows.append(obj.guid)

    def render(self, dialog, datatable, error_text, buttons_panel, selected_text, state):

        header = []
        get_row = None

        if self.search_obj_type == "user":
            h_email = "<span id='email' class='ug_datatable_title'>%s</span>" % (self.lang.get('ug.userstable.header.login'))
            h_fullname = "<span id='fullname' class='ug_datatable_title'>%s</span>" % (self.lang.get('ug.userstable.header.fullname'))
            header = ["id", h_fullname, h_email]

            def get_row(user): 
                return [
                    user.name[:USER_NAME_MAX_LENGTH]+MAX_LENGTH_STRING_END if len(user.name) > USER_NAME_MAX_LENGTH else user.name,
                    user.email[:USER_LOGIN_MAX_LENGTH]+MAX_LENGTH_STRING_END if len(user.email) > USER_LOGIN_MAX_LENGTH else user.email
                ]
        else:
            h_groupname = "<span id='groupname' class='ug_datatable_title'>%s</span>" % (self.lang.get('ug.userstable.header.group'))
            header = ["id", h_groupname]
            get_row = get_row = lambda group: [group.name[:GROUP_MAX_LENGTH]+MAX_LENGTH_STRING_END if len(group.name) > GROUP_MAX_LENGTH else group.name]

        selected_fields = self.lang["ug.group_dialog.users.count"] + " {}" if self.search_obj_type == "user" else self.lang["ug.user_dialog.groups.count"] + " {}"
        
        self.selected_rows = list(set(self.selected_rows))
        data = []
        append = data.append
        for obj in self.get_data():
            append([obj.guid] + get_row(obj))
        data.sort(key=lambda x: x[1].lower())
        if data:
            datatable.key = "id"
            datatable.hiddenfields = json.dumps(["id"])
            datatable.header = json.dumps(header)
            datatable.data = json.dumps(data)
            datatable.selectedrows = json.dumps(self.selected_rows)
            datatable.visible = "1"
            error_text.action("hide")
            selected_text.value = selected_fields.format(len(self.selected_rows))
            self.render_buttons_panel(buttons_panel, state)
            dialog.action('show')
        else:
            error_text.action("show")
            datatable.action("hide")
            dialog.action('show')


    def render_buttons_panel(self, buttons_panel, state):

        buttons_create = [
            {
                "type": "button",
                "label": "%s"%(self.lang["form.buttons.create"]),
                "className": "yes_btn",
                "name": "create_btn",
                "subtype": "submit"
            },
            {
                "type": "button",
                "label": "%s"%(self.lang["ug.user_dialog.create_cont"]),
                "className": "yes_btn",
                "name": "create_continue_btn",
                "subtype": "submit"
            },
            {
                "type": "button",
                "label": "%s"%(self.lang["form.buttons.cancel"]),
                "className": "no_btn",
                "name": "close_btn",
                "subtype": "button",
                "onclick": """window['dialog_win_'+$(this).closest(`[objtype='dialog_2']`).attr('id')].hide()"""
            }
        ]

        buttons_edit = [
            {
                "type": "button",
                "label": "%s"%(self.lang["form.buttons.save"]),
                "className": "yes_btn",
                "name": "save_btn",
                "subtype": "submit"
            },
            {
                "type": "button",
                "label": "%s"%(self.lang["form.buttons.cancel"]),
                "className": "no_btn",
                "name": "close_btn",
                "subtype": "button",
                "onclick": """window['dialog_win_'+$(this).closest(`[objtype='dialog_2']`).attr('id')].hide()"""
            }
        ]

        if buttons_panel is not None:
            buttons_panel.data = json.dumps(buttons_create if state == "create" else buttons_edit)
            buttons_panel.action('show')

    def apply_changes_to_object(self, obj, rows):
        obj_data = []
        get_data = remove_data = add_data = None

        if self.search_obj_type == "user":
            get_data = ProAdmin.application().get_users
            remove_data = obj.remove_user
            add_data = obj.add_user
            obj_data = obj.get_users()
        else:
            get_data = ProAdmin.application().get_groups
            remove_data = obj.remove_group
            add_data = obj.add_group
            obj_data = obj.get_groups()
        for o in obj_data:
            if o.guid not in rows:
                remove_data(o)
            else:
                rows.remove(o.guid)
        for guid in rows:
            search = get_data(guid=guid)
            if search:
                add_data(search[0])

import Appinmail


class WoleetEventHandler:
	def __init__(self, data):
		self.id = data["id"]
		self.file_name = data['name']
		self.state = data['state']
		self.created = data["created"]
		self.audit_events = data["auditEvents"]

		self.signees_data = data['authorizedSignees']
		self.signees_emails = [signe_data["email"] for signe_data in self.signees_data]

		self.events = []

	def get_first_user_events(self, email):
		return Appinmail.users.get_appinmail_user_woleet_events(email)

	def set_event_to_users(self, event):
		Appinmail.users.set_appinmail_users_woleet_event(
			self.signees_emails, event)

	def __generate_new_event(self, state, data):
		return {"id": self.id, "state": state, "data": data}

	def __creation_hadler(self):
		new_event = self.__generate_new_event(
			"created", {"date": self.created, "file_name": self.file_name})
		self.set_event_to_users(new_event)

	def __signing_hadler(self):
		for signee_data in self.signees_data:
			if signee_data.get("signedOn", False):
				new_event = self.__generate_new_event(
					"signed_by",
					{
						"date": signee_data["signedOn"],
						"user_name": signee_data["commonName"],
						"file_name": self.file_name
					}
				)
				if not new_event in self.events:
					self.set_event_to_users(new_event)

	def __cancellation_hadler(self):
		event_obj = None
		for event in self.audit_events:
			if event["type"] == self.state:
				event_obj = event
				break

		new_event = self.__generate_new_event(
			"canceled", {"date": event_obj["date"], "file_name": self.file_name})

		if not new_event in self.events:
			self.set_event_to_users(new_event)

	def __completed_hadler(self):
		# check for the last signer
		self.__signing_hadler()

		event_obj = None
		for event in self.audit_events:
			if event["type"] == self.state:
				event_obj = event
				break

		new_event = self.__generate_new_event(
			"completed", {"date": event_obj["date"], "file_name": self.file_name})

		if not new_event in self.events:
			self.set_event_to_users(new_event)

	def __determine_progress_handler(self):
		if not any(event["id"] == self.id for event in self.events):
			self.__creation_hadler()

		self.__signing_hadler()

	def handle(self):
		self.events = self.get_first_user_events(self.signees_emails[0])

		HANDLERS_BY_STATE = {
			"IN_PROGRESS": self.__determine_progress_handler,
			"CANCELED": self.__cancellation_hadler,
			"COMPLETED": self.__completed_hadler,
		}

		HANDLERS_BY_STATE[self.state]()
